INSERT INTO exercises (id, question, answer, topic_id)
VALUES (328, 'Сделайте HashSet из ключей HashMap.', 'HashSet<Object> set = new HashSet<>(map.keySet());', 4);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (329, 'Сделайте HashMap из HashSet<Map.Entry<K, V>>.',
