INSERT INTO exercises (id, question, answer, topic_id)
VALUES (378, 'Какие дополнительные методы для работы с ассоциативными массивами (maps) появились в Java 8?',
        '  putIfAbsent() добавляет пару «ключ-значение», только если ключ отсутствовал:map.putIfAbsent("a", "Aa");  forEach() принимает функцию, которая производит операцию над каждым элементом:map.forEach((k, v) -> System.out.println(v));  compute() создаёт или обновляет текущее значение на полученное в результате вычисления (возможно использовать ключ и текущее значение):map.compute("a", (k, v) -> String.valueOf(k).concat(v)); //["a", "aAa"]  computeIfPresent() если ключ существует, обновляет текущее значение на полученное в результате вычисления (возможно использовать ключ и текущее значение):map.computeIfPresent("a", (k, v) -> k.concat(v));  computeIfAbsent() если ключ отсутствует, создаёт его со значением, которое вычисляется (возможно использовать ключ):map.computeIfAbsent("a", k -> "A".concat(k)); //["a","Aa"]  getOrDefault() в случае отсутствия ключа, возвращает переданное значение по-умолчанию:map.getOrDefault("a", "not found");  merge() принимает ключ, значение и функцию, которая объединяет передаваемое и текущее значения. Если под заданным ключем значение отсутствует, то записывает туда передаваемое значение.map.merge("a", "z", (value, newValue) -> value.concat(newValue)); //["a","Aaz"]',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (379, 'Что такое LocalDateTime?',
        'LocalDateTime объединяет вместе LocaleDate и LocalTime, содержит дату и время в календарной системе ISO-8601 без привязки к часовому поясу. Время хранится с точностью до наносекунды. Содержит множество удобных методов, таких как plusMinutes, plusHours, isAfter, toSecondOfDay и т.д.',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (380, 'Что такое ZonedDateTime?',
        'java.time.ZonedDateTime — аналог java.util.Calendar, класс с самым полным объемом информации о временном контексте в календарной системе ISO-8601. Включает временную зону, поэтому все операции с временными сдвигами этот класс проводит с её учётом.',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (381, 'Как получить текущую дату с использованием Date Time API из Java 8?', 'LocalDate.now();', 5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (382, 'Как добавить 1 неделю, 1 месяц, 1 год, 10 лет к текущей дате с использованием Date Time API?',
        'LocalDate.now().plusWeeks(1);LocalDate.now().plusMonths(1);LocalDate.now().plusYears(1);LocalDate.now().plus(1, ChronoUnit.DECADES);',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (383, 'Как получить следующий вторник используя Date Time API?',
        'LocalDate.now().with(TemporalAdjusters.next(DayOfWeek.TUESDAY));', 5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (384, 'Как получить вторую субботу текущего месяца используя Date Time API?',
        'LocalDate    .of(LocalDate.now().getYear(), LocalDate.now().getMonth(), 1)    .with(TemporalAdjusters.nextOrSame(DayOfWeek.SATURDAY))    .with(TemporalAdjusters.next(DayOfWeek.SATURDAY));',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (385, 'Как получить текущее время с точностью до миллисекунд используя Date Time API?',
        'new Date().toInstant();', 5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (386, 'Как получить текущее время по местному времени с точностью до миллисекунд используя Date Time API?',
        'LocalDateTime.ofInstant(new Date().toInstant(), ZoneId.systemDefault());', 5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (387, 'Как определить повторяемую аннотацию?',
        'Чтобы определить повторяемую аннотацию, необходимо создать аннотацию-контейнер для списка повторяемых аннотаций и обозначить повторяемую мета-аннотацией @Repeatable:',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (388, 'Что такое Nashorn?',
        'Nashorn - это движок JavaScript, разрабатываемый на Java компанией Oracle. Призван дать возможность встраивать код JavaScript в приложения Java. В сравнении с Rhino, который поддерживается Mozilla Foundation, Nashorn обеспечивает от 2 до 10 раз более высокую производительность, так как он компилирует код и передает байт-код виртуальной машине Java непосредственно в памяти. Nashorn умеет компилировать код JavaScript и генерировать классы Java, которые загружаются специальным загрузчиком. Так же возможен вызов кода Java прямо из JavaScript.',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (389, 'Что такое jjs?',
        'jjs это утилита командной строки, которая позволяет исполнять программы на языке JavaScript прямо в консоли.',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (390, 'Какой класс появился в Java 8 для кодирования/декодирования данных?',
        'Base64 - потокобезопасный класс, который реализует кодировщик и декодировщик данных, используя схему кодирования base64 согласно RFC 4648 и RFC 2045.Base64 содержит 6 основных методов:getEncoder()/getDecoder() - возвращает кодировщик/декодировщик base64, соответствующий стандарту RFC 4648;getUrlEncoder()/getUrlDecoder() - возвращает URL-safe кодировщик/декодировщик base64, соответствующий стандарту RFC 4648;getMimeEncoder()/getMimeDecoder() - возвращает MIME кодировщик/декодировщик, соответствующий стандарту RFC 2045.',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (391, 'Как создать Base64 кодировщик и декодировщик?',
        '// EncodeString b64 = Base64.getEncoder().encodeToString("input".getBytes("utf-8")); //aW5wdXQ==// Decodenew String(Base64.getDecoder().decode("aW5wdXQ=="), "utf-8"); //input',
        5);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (392, '﻿Что такое JDBC?',
        'JDBC, Java DataBase Connectivity (соединение с базами данных на Java) — промышленный стандарт взаимодействия Java-приложений с различными СУБД. Реализован в виде пакета java.sql, входящего в состав Java SE.JDBC основан на концепции драйверов, которые позволяют получать соединение с базой данных по специально описанному URL. При загрузке драйвер регистрирует себя в системе и в дальнейшем автоматически вызывается, когда программа требует URL, содержащий протокол, за который этот драйвер отвечает.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (393, 'В чем заключаются преимущества использования JDBC?',
        'Преимуществами JDBC считают:  Лёгкость разработки: разработчик может не знать специфики базы данных, с которой работает;  Код практически не меняется, если компания переходит на другую базу данных (количество изменений зависит исключительно от различий между диалектами SQL);  Не нужно дополнительно устанавливать клиентскую программу;  К любой базе данных можно подсоединиться через легко описываемый URL.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (394, 'Что из себя представляет JDBC URL?',
        'JDBC URL состоит из:  <protocol>: (протокола) - всегда jdbc:.  <subprotocol>: (подпротокола) - это имя драйвера или имя механизма соединения с базой данных. Подпротокол может поддерживаться одним или несколькими драйверами. Лежащий на поверхности пример подпротокола - это "odbc", отведенный для URL, обозначающих имя источника данных ODBC. В случае необходимости использовать сервис имен (т.е. имя базы данных в JDBC URL не будет действительным именем базы данных), то подпротоколом может выступать сервис имен.  <subname> (подимени) - это идентификатор базы данных. Значение подимени может менятся в зависимости от подпротокола, и может также иметь под-подимя с синтаксисом, определяемым разработчиком драйвера. Назначение подимени - это предоставление всей информации, необходимой для поиска базы данных. Например, если база данных находится в Интернет, то в состав подимени JDBC URL должен быть включен сетевой адрес, подчиняющийся следующим соглашениям: //<hostname>:<port>/<subsubname.Пример JDBC URL для подключения к MySQL базе данных «Test» расположенной по адресу localhost и ожидающей соединений по порту 3306: jdbc:mysql://localhost:3306/Test',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (395, 'Из каких частей стоит JDBC?',
        'JDBC состоит из двух частей:  JDBC API, который содержит набор классов и интерфейсов, определяющих доступ к базам данных. Эти классы и методы объявлены в двух пакетах - java.sql и javax.sql;  JDBC-драйвер, компонент, специфичный для каждой базы данных.JDBC превращает вызовы уровня API в «родные» команды того или иного сервера баз данных.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (396, 'Перечислите основные классы и интерфейсы JDBC.',
        '  java.sql.DriverManager - позволяет загрузить и зарегистрировать необходимый JDBC-драйвер, а затем получить соединение с базой данных.  javax.sql.DataSource - решает те же задачи, что и DriverManager, но более удобным и универсальным образом. Существуют также javax.sql.ConnectionPoolDataSource и javax.sq1.XADataSource задача которых - обеспечение поддержки пула соединений.  java.sql.Connection  - обеспечивает формирование запросов к источнику данных и управление транзакциями. Также предусмотрены интерфейсы javax.sql.PooledConnection и javax.sql.XAConnection.  java.sql.Statement , java.sql.PreparedStatement и java.sql.CallableStatement  - эти интерфейсы позволяют отправить запрос к источнику данных.  java.sql.ResultSet  - объявляет методы, которые позволяют перемещаться по набору данных и считывать значения отдельных полей в текущей записи.  java.sql.ResultSetMetaData  - позволяет получить информацию о структуре набора данных.  java.sql.DatabaseMetaData - позволяет получить информацию о структуре источника данных.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (397, 'Перечислите основные типы данных используемые в JDBC. Как они связаны с типами Java?',
        'JDBC TypeJava Object TypeCHARStringVARCHARStringLONGVARCHARStringNUMERICjava.math.BigDecimalDECIMALjava.math.BigDecimalBITBooleanTINYINTIntegerSMALLINTIntegerINTEGERIntegerBIGINTLongREALFloatFLOATDoubleDOUBLEDoubleBINARYbyte[]VARBINARYbyte[]LONGVARBINARYbyte[]DATEjava.sql.DateTIMEjava.sql.TimeTIMESTAMPjava.sql.TimestampCLOBClobBLOBBlobARRAYArraySTRUCTStructREFRefDISTINCTсопоставление базового типаJAVA_OBJECTбазовый класс Java',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (398, 'Опишите основные этапы работы с базой данных при использовании JDBC.',
        '  Регистрация драйверов;  Установление соединения с базой данных;  Создание запроса(ов) к базе данных;  Выполнение запроса(ов) к базе данных;  Обработка результата(ов);  Закрытие соединения с базой данных.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (399, 'Как зарегистрировать драйвер JDBC?',
        'Регистрацию драйвера можно осуществить несколькими способами:  java.sql.DriverManager.registerDriver(%объект класса драйвера%).  Class.forName(«полное имя класса драйвера»).newInstance().  Class.forName(«полное имя класса драйвера»);',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (400, 'Как установить соединение с базой данных?',
        'Для установки соединения с базой данных используется статический вызов java.sql.DriverManager.getConnection(...) .В качестве параметра может передаваться:  URL базы данныхstatic Connection getConnection(String url)  URL базы данных и набор свойств для инициализацииstatic Connection getConnection(String url, Properties info)  URL базы данных, имя пользователя и парольstatic Connection getConnection(String url, String user, String password)В результате вызова будет установлено соединение с базой данных и создан объект класса java.sql.Connection - своеобразная «сессия», внутри контекста которой и будет происходить дальнейшая работа с базой данных.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (401, 'Какие уровни изоляции транзакций поддерживаются в JDBC?',
        'Уровень изолированности транзакций — значение, определяющее уровень, при котором в транзакции допускаются несогласованные данные, то есть степень изолированности одной транзакции от другой. Более высокий уровень изолированности повышает точность данных, но при этом может снижаться количество параллельно выполняемых транзакций. С другой стороны, более низкий уровень изолированности позволяет выполнять больше параллельных транзакций, но снижает точность данных.Во время использования транзакций, для обеспечения целостности данных, СУБД использует блокировки, чтобы заблокировать доступ других обращений к данным, участвующим в транзакции. Такие блокировки необходимы, чтобы предотвратить:  «грязное» чтение (dirty read) — чтение данных, добавленных или изменённых транзакцией, которая впоследствии не подтвердится (откатится);  неповторяющееся чтение (non-repeatable read) — при повторном чтении в рамках одной транзакции ранее прочитанные данные оказываются изменёнными;  фантомное чтение (phantom reads) — ситуация, когда при повторном чтении в рамках одной транзакции одна и та же выборка дает разные множества строк.Уровни изоляции транзакций определены в виде констант интерфейса java.sql.Connection:  TRANSACTION_NONE – драйвер не поддерживает транзакции;  TRANSACTION_READ_UNCOMMITTED – позволяет транзакциям видеть несохраненные изменения данных: разрешает грязное, непроверяющееся и фантомное чтения;  TRANSACTION_READ_COMMITTED – любое изменение, сделанное в транзакции, не видно вне неё, пока она не сохранена: предотвращает грязное чтение, но разрешает непроверяющееся и фантомное;  TRANSACTION_REPEATABLE_READ – запрещает грязное и непроверяющееся, фантомное чтение разрешено;  TRANSACTION_SERIALIZABLE – грязное, непроверяющееся и фантомное чтения запрещены.NB! Сервер базы данных может не поддерживать все уровни изоляции. Интерфейс java.sql.DatabaseMetaData предоставляет информацию об уровнях изолированности транзакций, которые поддерживаются данной СУБД.Уровень изоляции транзакции используемый СУБД можно задать с помощью метода setTransactionIsolation() объекта java.sql.Connection. Получить информацию о применяемом уровне изоляции поможет метод getTransactionIsolation().',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (402, 'При помощи чего формируются запросы к базе данных?',
        'Для выполнения запросов к базе данных в Java используются три интерфейса:  java.sql.Statement - для операторов SQL без параметров;  java.sql.PreparedStatement - для операторов SQL с параметрами и часто выполняемых операторов;  java.sql.CallableStatement -  для исполнения хранимых в базе процедур.Объекты-носители интерфейсов создаются при помощи методов объекта java.sql.Connection:  java.sql.createStatement() возвращает объект Statement;  java.sql.prepareStatement() возвращает объект PreparedStatement;  java.sql.prepareCall() возвращает объект CallableStatement;',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (403, 'Чем отличается Statement от PreparedStatement?',
        '  Statement: используется для простых случаев запроса без параметров.  PreparedStatement: предварительно компилирует запрос, который может содержать входные параметры и выполняться несколько раз с разным набором этих параметров.Перед выполнением СУБД разбирает каждый запрос, оптимизирует его и создает «план» (query plan) его выполнения. Если один и тот же запрос выполняется несколько раз, то СУБД в состоянии кэшировать план его выполнения и не производить этапов разборки и оптимизации повторно. Благодаря этому запрос выполняется быстрее.Суммируя: PreparedStatement выгодно отличается от Statement тем, что при повторном использовании с одним или несколькими наборами параметров позволяет получить преимущества заранее прекомпилированного и кэшированного запроса, помогая при этом избежать SQL Injection.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (404, 'Как осуществляется запрос к базе данных и обработка результатов?',
        'Выполнение запросов осуществляется при помощи вызова методов объекта, реализующего интерфейс java.sql.Statement:  executeQuery() -  для запросов, результатом которых является один набор значений, например запросов SELECT. Результатом выполнения является объект класса java.sql.ResultSet;  executeUpdate() - для выполнения операторов INSERT, UPDATE или DELETE, а также для операторов DDL (Data Definition Language). Метод возвращает целое число, показывающее, сколько записей было модифицировано;  execute() – исполняет SQL-команды, которые могут возвращать различные результаты. Например, может использоваться для операции CREATE TABLE. Возвращает true, если первый результат содержит ResultSet и false, если первый результат - это количество модифицированных записей или результат отсутствует. Чтобы получить первый результат необходимо вызвать метод getResultSet() или getUpdateCount(). Остальные результаты доступны через вызов getMoreResults(), который при необходимости может быть произведён многократно.Объект с интерфейсом java.sql.ResultSet хранит в себе результат запроса к базе данных - некий набор данных, внутри которого есть курсор, указывающий на один из элементов набора данных - текущую запись.Используя курсор можно перемещаться по набору данных при помощи метода next().NB! Сразу после получения набора данных его курсор находится перед первой записью и чтобы сделать её текущей необходимо вызвать метод next().Содержание полей текущей записи доступно через вызовы методов getInt(), getFloat(), getString(), getDate() и им подобных.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (405, 'Как вызвать хранимую процедуру?',
        'Хранимые процедуры – это именованный набор операторов SQL хранящийся на сервере. Такую процедуру можно вызвать из Java-класса с помощью вызова методов объекта реализующего интерфейс java.sql.Statement.Выбор объекта зависит от характеристик хранимой процедуры:  без параметров → Statement  с входными параметрами → PreparedStatement  с входными и выходными параметрами → CallableStatementЕсли неизвестно, как была определена хранимая процедура, для получения информации о хранимой процедуре (например, имен и типов параметров) можно использовать методы java.sql.DatabaseMetaData позволяющие получить информацию о структуре источника данных.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (406, 'Как закрыть соединение с базой данных?',
        'Соединение с базой данной закрывается вызовом метода close() у соответствующего объекта java.sql.Connection или посредством использования механизма try-with-resources при создании такого объекта, появившегося в Java 7.NB! Предварительно необходимо закрыть все запросы созданные этим соединением.',
        6);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (407, '﻿Что такое «SQL»?',
        'SQL, Structured query language («язык структурированных запросов») — формальный непроцедурный язык программирования, применяемый для создания, модификации и управления данными в произвольной реляционной базе данных, управляемой соответствующей системой управления базами данных (СУБД).',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (408, 'Какие существуют операторы SQL?',
        'операторы определения данных (Data Definition Language, DDL):  CREATE создает объект БД (базу, таблицу, представление, пользователя и т. д.),  ALTER изменяет объект,  DROP удаляет объект;операторы манипуляции данными (Data Manipulation Language, DML):  SELECT выбирает данные, удовлетворяющие заданным условиям,  INSERT добавляет новые данные,  UPDATE изменяет существующие данные,  DELETE удаляет данные;операторы определения доступа к данным (Data Control Language, DCL):  GRANT предоставляет пользователю (группе) разрешения на определенные операции с объектом,  REVOKE отзывает ранее выданные разрешения,  DENY задает запрет, имеющий приоритет над разрешением;операторы управления транзакциями (Transaction Control Language, TCL):  COMMIT применяет транзакцию,  ROLLBACK откатывает все изменения, сделанные в контексте текущей транзакции,  SAVEPOINT разбивает транзакцию на более мелкие.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (409, 'Что означает NULL в SQL?',
        'NULL - специальное значение (псевдозначение), которое может быть записано в поле таблицы базы данных. NULL соответствует понятию «пустое поле», то есть «поле, не содержащее никакого значения».NULL означает отсутствие, неизвестность информации. Значение NULL не является значением в полном смысле слова: по определению оно означает отсутствие значения и не принадлежит ни одному типу данных. Поэтому NULL не равно ни логическому значению FALSE, ни пустой строке, ни 0. При сравнении NULL с любым значением будет получен результат NULL, а не FALSE и не 0. Более того, NULL не равно NULL!',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (410, 'Что такое «временная таблица»? Для чего она используется?',
        'Временная таблица - это объект базы данных, который хранится и управляется системой базы данных на временной основе. Они могут быть локальными или глобальными. Используется для сохранения результатов вызова хранимой процедуры, уменьшение числа строк при соединениях, агрегирование данных из различных источников или как замена курсоров и параметризованных представлений.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (411, 'Что такое «представление» (view) и для чего оно применяется?',
        'Представление, View - виртуальная таблица, представляющая данные одной или более таблиц альтернативным образом.В действительности представление – всего лишь результат выполнения оператора SELECT, который хранится в структуре памяти, напоминающей SQL таблицу. Они работают в запросах и операторах DML точно также как и основные таблицы, но не содержат никаких собственных данных. Представления значительно расширяют возможности управления данными. Это способ дать публичный доступ к некоторой (но не всей) информации в таблице.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (412, 'Каков общий синтаксис оператора SELECT?',
        'SELECT - оператор DML SQL, возвращающий набор данных (выборку) из базы данных, удовлетворяющих заданному условию. Имеет следующую структуру:',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (413, 'Что такое JOIN?',
        'JOIN - оператор языка SQL, который является реализацией операции соединения реляционной алгебры. Предназначен для обеспечения выборки данных из двух таблиц и включения этих данных в один результирующий набор.Особенностями операции соединения являются следующее:  в схему таблицы-результата входят столбцы обеих исходных таблиц (таблиц-операндов), то есть схема результата является «сцеплением» схем операндов;  каждая строка таблицы-результата является «сцеплением» строки из одной таблицы-операнда со строкой второй таблицы-операнда;  при необходимости соединения не двух, а нескольких таблиц, операция соединения применяется несколько раз (последовательно).',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (414, 'Какие существуют типы JOIN?',
        '(INNER) JOINРезультатом объединения таблиц являются записи, общие для левой и правой таблиц. Порядок таблиц для оператора не важен, поскольку оператор является симметричным.LEFT (OUTER) JOINПроизводит выбор всех записей первой таблицы и соответствующих им записей второй таблицы. Если записи во второй таблице не найдены, то вместо них подставляется пустой результат (NULL). Порядок таблиц для оператора важен, поскольку оператор не является симметричным.RIGHT (OUTER) JOINLEFT JOIN с операндами, расставленными в обратном порядке. Порядок таблиц для оператора важен, поскольку оператор не является симметричным.FULL (OUTER) JOINРезультатом объединения таблиц являются все записи, которые присутствуют в таблицах. Порядок таблиц для оператора не важен, поскольку оператор является симметричным.CROSS JOIN (декартово произведение)При выборе каждая строка одной таблицы объединяется с каждой строкой второй таблицы, давая тем самым все возможные сочетания строк двух таблиц. Порядок таблиц для оператора не важен, поскольку оператор является симметричным.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (415, 'Что лучше использовать JOIN или подзапросы?',
        'Обычно лучше использовать JOIN, поскольку в большинстве случаев он более понятен и лучше оптимизируется СУБД (но 100% этого гарантировать нельзя). Так же JOIN имеет заметное преимущество над подзапросами в случае, когда список выбора SELECT содержит столбцы более чем из одной таблицы.Подзапросы лучше использовать в случаях, когда нужно вычислять агрегатные значения и использовать их для сравнений во внешних запросах.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (416, 'Для чего используется оператор HAVING?',
        'HAVING используется для фильтрации результата GROUP BY по заданным логическим условиям.', 7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (417, 'В чем различие между операторами HAVING и WHERE?',
        'Основное отличие ''WHERE'' от ''HAVING'' заключается в том, что ''WHERE'' сначала выбирает строки, а затем группирует их и вычисляет агрегатные функции (таким образом, она отбирает строки для вычисления агрегатов), тогда как ''HAVING'' отбирает строки групп после группировки и вычисления агрегатных функций. Как следствие, предложение ''WHERE'' не должно содержать агрегатных функций; не имеет смысла использовать агрегатные функции для определения строк для вычисления агрегатных функций. Предложение ''HAVING'', напротив, всегда содержит агрегатные функции. (Строго говоря, вы можете написать предложение ''HAVING'', не используя агрегаты, но это редко бывает полезно. То же самое условие может работать более эффективно на стадии ''WHERE''.)',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (418, 'Для чего используется оператор ORDER BY?',
        'ORDER BY упорядочивает вывод запроса согласно значениям в том или ином количестве выбранных столбцов. Многочисленные столбцы упорядочиваются один внутри другого. Возможно определять возрастание ASC или убывание DESC для каждого столбца. По умолчанию установлено - возрастание.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (419, 'Для чего используется оператор GROUP BY?',
        'GROUP BY используется для агрегации записей результата по заданным признакам-атрибутам.', 7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (420, 'Как GROUP BY обрабатывает значение NULL?',
        'При использовании GROUP BY все значения NULL считаются равными.', 7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (421, 'В чем разница между операторами GROUP BY и DISTINCT?',
        'DISTINCT указывает, что для вычислений используются только уникальные значения столбца. NULL считается как отдельное значение.GROUP BY создает отдельную группу для всех возможных значений (включая значение NULL).Если нужно удалить только дубликаты лучше использовать DISTINCT, GROUP BY лучше использовать для определения групп записей, к которым могут применяться агрегатные функции.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (422, 'Перечислите основные агрегатные функции.',
        'Агрегатных функции - функции, которые берут группы значений и сводят их к одиночному значению.SQL предоставляет несколько агрегатных функций:COUNT - производит подсчет записей, удовлетворяющих условию запроса;SUM - вычисляет арифметическую сумму всех значений колонки;AVG - вычисляет среднее арифметическое всех значений;MAX - определяет наибольшее из всех выбранных значений;MIN - определяет наименьшее из всех выбранных значений.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (423, 'В чем разница между COUNT(*) и COUNT({column})?',
        'COUNT (*) подсчитывает количество записей в таблице, не игнорируя значение NULL, поскольку эта функция оперирует записями, а не столбцами.COUNT ({column}) подсчитывает количество значений в {column}. При подсчете количества значений столбца эта форма функции COUNT не принимает во внимание значение NULL.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (424, 'Что делает оператор EXISTS?',
        'EXISTS берет подзапрос, как аргумент, и оценивает его как TRUE, если подзапрос возвращает какие-либо записи и FALSE, если нет.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (425, 'Для чего используются операторы IN, BETWEEN, LIKE?',
        'IN - определяет набор значений.SELECT * FROM Persons WHERE name IN (''Ivan'',''Petr'',''Pavel'');BETWEEN определяет диапазон значений. В отличие от IN, BETWEEN чувствителен к порядку, и первое значение в предложении должно быть первым по алфавитному или числовому порядку.SELECT * FROM Persons WHERE age BETWEEN 20 AND 25;LIKE применим только к полям типа CHAR или VARCHAR, с которыми он используется чтобы находить подстроки. В качестве условия используются символы шаблонизации (wildkards) - специальные символы, которые могут соответствовать чему-нибудь:  _ замещает любой одиночный символ. Например, ''b_t'' будет соответствовать словам ''bat'' или ''bit'', но не будет соответствовать ''brat''.  % замещает последовательность любого числа символов. Например ''%p%t'' будет соответствовать словам ''put'', ''posit'', или ''opt'', но не ''spite''.SELECT * FROM UNIVERSITY WHERE NAME LIKE ''%o'';',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (426, 'Для чего применяется ключевое слово UNION?',
        'В языке SQL ключевое слово UNION применяется для объединения результатов двух SQL-запросов в единую таблицу, состоящую из схожих записей. Оба запроса должны возвращать одинаковое число столбцов и совместимые типы данных в соответствующих столбцах. Необходимо отметить, что UNION сам по себе не гарантирует порядок записей. Записи из второго запроса могут оказаться в начале, в конце или вообще перемешаться с записями из первого запроса. В случаях, когда требуется определенный порядок, необходимо использовать ORDER BY.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (427, 'Какие ограничения на целостность данных существуют в SQL?',
        'PRIMARY KEY - набор полей (1 или более), значения которых образуют уникальную комбинацию и используются для однозначной идентификации записи в таблице. Для таблицы может быть создано только одно такое ограничение. Данное ограничение используется для обеспечения целостности сущности, которая описана таблицей.CHECK используется для ограничения множества значений, которые могут быть помещены в данный столбец. Это ограничение используется для обеспечения целостности предметной области, которую описывают таблицы в базе.UNIQUE обеспечивает отсутствие дубликатов в столбце или наборе столбцов.FOREIGN KEY защищает от действий, которые могут нарушить связи между таблицами. FOREIGN KEY в одной таблице указывает на PRIMARY KEY в другой. Поэтому данное ограничение нацелено на то, чтобы не было записей FOREIGN KEY, которым не отвечают записи PRIMARY KEY.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (428, 'Какие отличия между ограничениями PRIMARY и UNIQUE?',
        'По умолчанию ограничение PRIMARY создает кластерный индекс на столбце, а UNIQUE - некластерный. Другим отличием является то, что PRIMARY не разрешает NULL записей, в то время как UNIQUE разрешает одну (а в некоторых СУБД несколько) NULL запись.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (429, 'Может ли значение в столбце, на который наложено ограничение FOREIGN KEY, равняться NULL?',
        'Может, если на данный столбец не наложено ограничение NOT NULL.', 7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (430, 'Как создать индекс?',
        'Индекс можно создать либо с помощью выражения CREATE INDEX:CREATE INDEX index_name ON table_name (column_name)либо указав ограничение целостности в виде уникального UNIQUE или первичного PRIMARY ключа в операторе создания таблицы CREATE TABLE.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (431, 'Что делает оператор MERGE?',
        'MERGE позволяет осуществить слияние данных одной таблицы с данными другой таблицы. При слиянии таблиц проверяется условие, и если оно истинно, то выполняется UPDATE, а если нет - INSERT. При этом изменять поля таблицы в секции UPDATE, по которым идет связывание двух таблиц, нельзя.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (432, 'В чем отличие между операторами DELETE и TRUNCATE?',
        'DELETE - оператор DML, удаляет записи из таблицы, которые удовлетворяют критерию WHERE при этом задействуются триггеры, ограничения и т.д.TRUNCATE - DDL оператор (удаляет таблицу и создает ее заново. Причем если на эту таблицу есть ссылки FOREGIN KEY или таблица используется в репликации, то пересоздать такую таблицу не получится).',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (433, 'Что такое «хранимая процедура»?',
        'Хранимая процедура — объект базы данных, представляющий собой набор SQL-инструкций, который хранится на сервере. Хранимые процедуры очень похожи на обыкновенные процедуры языков высокого уровня, у них могут быть входные и выходные параметры и локальные переменные, в них могут производиться числовые вычисления и операции над символьными данными, результаты которых могут присваиваться переменным и параметрам. В хранимых процедурах могут выполняться стандартные операции с базами данных (как DDL, так и DML). Кроме того, в хранимых процедурах возможны циклы и ветвления, то есть в них могут использоваться инструкции управления процессом исполнения.Хранимые процедуры позволяют повысить производительность, расширяют возможности программирования и поддерживают функции безопасности данных. В большинстве СУБД при первом запуске хранимой процедуры она компилируется (выполняется синтаксический анализ и генерируется план доступа к данным) и в дальнейшем её обработка осуществляется быстрее.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (434, 'Что такое «триггер»?',
        'Триггер (trigger) — это хранимая процедура особого типа, которую пользователь не вызывает непосредственно, а исполнение которой обусловлено действием по модификации данных: добавлением, удалением или изменением данных в заданной таблице реляционной базы данных. Триггеры применяются для обеспечения целостности данных и реализации сложной бизнес-логики. Триггер запускается сервером автоматически и все производимые им модификации данных рассматриваются как выполняемые в транзакции, в которой выполнено действие, вызвавшее срабатывание триггера. Соответственно, в случае обнаружения ошибки или нарушения целостности данных может произойти откат этой транзакции.Момент запуска триггера определяется с помощью ключевых слов BEFORE (триггер запускается до выполнения связанного с ним события) или AFTER (после события). В случае, если триггер вызывается до события, он может внести изменения в модифицируемую событием запись. Кроме того, триггеры могут быть привязаны не к таблице, а к представлению (VIEW). В этом случае с их помощью реализуется механизм «обновляемого представления». В этом случае ключевые слова BEFORE и AFTER влияют лишь на последовательность вызова триггеров, так как собственно событие (удаление, вставка или обновление) не происходит.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (435, 'Что такое «курсор»?',
        'Курсор — это объект базы данных, который позволяет приложениям работать с записями «по-одной», а не сразу с множеством, как это делается в обычных SQL командах.Порядок работы с курсором такой:  Определить курсор (DECLARE)  Открыть курсор (OPEN)  Получить запись из курсора (FETCH)  Обработать запись...  Закрыть курсор (CLOSE)  Удалить ссылку курсора (DEALLOCATE). Когда удаляется последняя ссылка курсора, SQL освобождает структуры данных, составляющие курсор.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (436, 'Опишите разницу типов данных DATETIME и TIMESTAMP.',
        'DATETIME предназначен для хранения целого числа: YYYYMMDDHHMMSS. И это время не зависит от временной зоны, настроенной на сервере.Размер: 8 байтTIMESTAMP хранит значение равное количеству секунд, прошедших с полуночи 1 января 1970 года по усреднённому времени Гринвича. При получении из базы отображается с учётом часового пояса. Размер: 4 байта',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (437, 'Для каких числовых типов недопустимо использовать операции сложения/вычитания?',
        'В качестве операндов операций сложения и вычитания нельзя использовать числовой тип BIT.', 7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (438, 'Какое назначение у операторов PIVOT и UNPIVOT в Transact-SQL?',
        'PIVOT и UNPIVOT являются нестандартными реляционными операторами, которые поддерживаются Transact-SQL.Оператор PIVOT разворачивает возвращающее табличное значение выражение, преобразуя уникальные значения одного столбца выражения в несколько выходных столбцов, а также, в случае необходимости, объединяет оставшиеся повторяющиеся значения столбца и отображает их в выходных данных. Оператор UNPIVOT производит действия, обратные PIVOT, преобразуя столбцы возвращающего табличное значение выражения в значения столбца.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (439, 'Расскажите об основных функциях ранжирования в Transact-SQL.',
        'Ранжирующие функции - это функции, которые возвращают значение для каждой записи группы в результирующем наборе данных. На практике они могут быть использованы, например, для простой нумерации списка, составления рейтинга или постраничной навигации.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (440, 'Для чего используются операторы INTERSECT, EXCEPT в Transact-SQL?',
        'Оператор EXCEPT возвращает уникальные записи из левого входного запроса, которые не выводятся правым входным запросом.Оператор INTERSECT возвращает уникальные записи, выводимые левым и правым входными запросами.',
        7);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (441, '﻿Что такое WWW?',
        'WWW, World Wide Web (Всемирная паутина) — распределённая система, предоставляющая доступ к связанным между собой документам, расположенным на различных компьютерах, подключённых к Интернету. Для обозначения этого термина также используют слово web.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (442, 'Что такое W3C?',
        'W3C, World Wide Web Consortium (Консорциум Всемирной паутины) — организация, разрабатывающая и внедряющая технологические стандарты для WWW.W3C разрабатывает для Интернета единые принципы и стандарты, называемые «рекомендациями» (W3C Recommendations), которые затем внедряются производителями программ и оборудования. Таким образом достигается совместимость между программными продуктами и аппаратурой различных компаний.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (443, 'Какие существуют уровни модели OSI?',
        '#Уровень (layer)Тип данных (PDU)ФункцииПримеры7Прикладной (application)-Доступ к сетевым службамHTTP, FTP6Представительский (представления) (presentation)-Представление и шифрование данныхASCII, JPEG5Сеансовый (session)-Управление сеансом связиRPC, PAP4Транспортный (transport)Сегменты(segment) / Дейтаграммы(datagram)Прямая связь между конечными пунктами и надежностьTCP, UDP3Сетевой (network)Пакеты (packet)Определение маршрута и логическая адресацияIP, AppleTalk2Канальный (data link)Биты (bit) / Кадры (frame)Физическая адресацияEthernet, IEEE 802.2, L2TP1Физический (physical)Биты (bit)Работа со средой передачи, сигналами и двоичными даннымиUSB, витая пара',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (444, 'Что такое TCP/IP?',
        'TCP/IP - это два основных сетевых протокола Internet. Часто это название используют и для обозначения сетей, работающих на их основе.IP (Internet Protocol) - маршрутизируемый протокол, отвечающий за IP-адресацию, маршрутизацию, фрагментацию и восстановление пакетов. В его задачу входит продвижение пакета между сетями – от одного маршрутизатора до другого и тех пор, пока пакет не попадет в сеть назначения. В отличие от протоколов прикладного и транспортного уровней, протокол IP разворачивается не только на хостах, но и на всех шлюзах (маршрутизаторах). Этот протокол работает без установления соединения и без гарантированной доставки.В настоящее время используются следующие две версии протокола IP:  IPv6 — IP-адрес имеет разрядность 128 бит и записывается в виде восьми 16-битных полей, с использованием шестнадцатеричной системы счисления и с возможностью сокращения двух и более последовательных нулевых полей до ::, например: 2001:db8:42::1337:cafe  IPv4 — IP-адрес имеет разрядность 32 бита и записывается в виде четырех десятичных чисел в диапазоне 0 … 255 через точку, например: 192.0.2.34.TCP (Transfer Control Protocol) - протокол, обеспечивающий надежную, требующую логического соединения связь между двумя компьютерами. Отвечает за установление соединения, упорядочивание посылаемых пакетов и восстановление пакетов, потерянных в процессе передачи.Стек протоколов TCP/IP включает в себя четыре уровня:1. канальный уровень (link layer) - например Ethernet, IEEE 802.11 Wireless Ethernet, физическая среда и принципы кодирования информации2. сетевой уровень (Internet layer) - например IP3. транспортный уровень (transport layer) - например TCP, UDP4. прикладной уровень (application layer) - например HTTP, FTP, DNSTCP-соединение двух узлов начинается с handshake (рукопожатия):  Узел A посылает узлу B специальный пакет SYN — приглашение к соединению  B отвечает пакетом SYN-ACK — согласием об установлении соединения  A посылает пакет ACK — подтверждение, что согласие полученоПосле этого TCP соединение считается установленным и приложения, работающие в этих узлах, могут посылать друг другу пакеты с данными.В заголовке TCP/IP пакета указывается:  IP-адрес отправителя  IP-адрес получателя  Номер порта',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (445, 'Что такое UDP?',
        'UDP, User Datagram Protocol (Протокол пользовательских датаграмм) — протокол, который обеспечивает доставку без требований соединения с удаленным модулем UDP и обязательного подтверждения получения.К заголовку IP-пакета UDP добавляет всего четыре поля по 2 байта каждое:1. поле порта источника (source port)2. поле порта пункта назначения (destination port)3. поле длины (length)4. поле контрольной суммы (checksum)Поля «порт источника» и «контрольная сумма» не являются обязательными для использования в IPv4. В IPv6 необязательно только поле «порт отправителя».UDP используется DNS, SNMP, DHCP и другими приложениями.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (446, 'Чем отличаются TCP и UDP?',
        'TCP — ориентированный на соединение протокол, что означает необходимость «рукопожатия» для установки соединения между двумя хостами. Как только соединение установлено, пользователи могут отправлять данные в обоих направлениях.  Надёжность — TCP управляет подтверждением, повторной передачей и тайм-аутом сообщений. Производятся многочисленные попытки доставить сообщение. Если оно потеряется на пути, сервер вновь запросит потерянную часть. В TCP нет ни пропавших данных, ни (в случае многочисленных тайм-аутов) разорванных соединений.  Упорядоченность — если два сообщения последовательно отправлены, первое сообщение достигнет приложения-получателя первым. Если участки данных приходят в неверном порядке, TCP отправляет неупорядоченные данные в буфер до тех пор, пока все данные не могут быть упорядочены и переданы приложению.  Тяжеловесность — TCP необходимо три пакета для установки соединения перед тем, как отправить данные. TCP следит за надёжностью и перегрузками.  Потоковость — данные читаются как поток байтов, не передается никаких особых обозначений для границ сообщения или сегментов.UDP — более простой, основанный на сообщениях протокол без установления соединения. Протоколы такого типа не устанавливают выделенного соединения между двумя хостами. Связь достигается путём передачи информации в одном направлении от источника к получателю без проверки готовности или состояния получателя.  Ненадёжность — когда сообщение посылается, неизвестно, достигнет ли оно своего назначения — оно может потеряться по пути. Нет таких понятий как подтверждение, повторная передача, тайм-аут.  Неупорядоченность — если два сообщения отправлены одному получателю, то порядок их достижения цели не может быть предугадан.  Легковесность — никакого упорядочивания сообщений, никакого отслеживания соединений и т. д. Это лишь транспортный уровень.  Датаграммы — пакеты посылаются по отдельности и проверяются на целостность только если они прибыли. Пакеты имеют определенные границы, которые соблюдаются после получения, то есть операция чтения на получателе выдаст сообщение таким, каким оно было изначально послано.  Отсутствие контроля перегрузок — для приложений с большой пропускной способностью существует шанс вызвать коллапс перегрузок, если только они не реализуют меры контроля на прикладном уровне.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (447, 'Что такое протокол передачи данных? Какие протоколы вы знаете?',
        'Протокол передачи данных — набор соглашений интерфейса логического уровня, которые определяют обмен данными между различными программами. Эти соглашения задают единообразный способ передачи сообщений и обработки ошибок при взаимодействии программного обеспечения разнесённой в пространстве аппаратуры, соединённой тем или иным интерфейсом.Наиболее известные протоколы передачи данных:  HTTP (Hyper Text Transfer Protocol)  FTP (File Transfer Protocol)  POP3 (Post Office Protocol)  SMTP (Simple Mail Transfer Protocol)  TELNET (TErminaL NETwork)',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (448, 'Что такое HTTP и HTTPS? Чем они отличаются?',
        'HTTP, HyperText Transfer Protocol (Протокол передачи гипертекста) — протокол прикладного уровня передачи данных.Основой HTTP является технология «клиент-сервер»:  Потребители (клиенты), которые инициируют соединение и посылают запрос;  Поставщики (серверы), которые ожидают соединения для получения запроса, производят необходимые действия и возвращают обратно сообщение с результатом.Для идентификации ресурсов HTTP использует глобальные URI.HTTP не сохраняет своего состояния. Это означает отсутствие сохранения промежуточного состояния между парами «запрос-ответ».Структура протокола:1. Стартовая строка (starting line) — определяет тип сообщения;2. Заголовки (headers) — характеризуют тело сообщения, параметры передачи и прочие сведения;3. Тело сообщения (message body) — непосредственно данные сообщения. Обязательно должно отделяться от заголовков пустой строкой.Заголовки и тело сообщения могут отсутствовать, но стартовая строка является обязательным элементом, так как указывает на тип запроса/ответа.HTTPS, HyperText Transfer Protocol Secure — расширение протокола HTTP, поддерживающее шифрование. Данные, передаваемые по протоколу HTTPS, «упаковываются» в криптографический протокол SSL или TLS, что обеспечивает защиту от атак, основанных на прослушивании сетевого соединения (при условии, что будут использоваться шифрующие средства и сертификат сервера проверен и ему доверяют).Различия HTTP и HTTPS:  HTTPS является расширением HTTP.  HTTP использует не зашифрованное соединение. Соединение по HTTPS поддерживает шифрование.  Работа по HTTP быстрей и менее ресурсоёмко, т.к. шифрование HTTPS требует дополнительных затрат.  Порты по умолчанию: в случае HTTP это TCP-порт 80, для HTTPS - TCP-порт 443.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (449, 'Что такое FTP?',
        'FTP, File Transfer Protocol (Протокол передачи файлов) — протокол передачи файлов между компьютерами в сети TCP. С его помощью можно подключаться к FTP-серверам, просматривать содержимое их каталогов и загружать файлы с сервера или на сервер. Протокол построен на архитектуре «клиент-сервер» и использует разные сетевые соединения для передачи команд и данных между клиентом и сервером.По умолчанию использует TCP-порт 21.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (450, 'Чем отличаются методы GET и POST?',
        'GET передает данные серверу, используя URL, тогда как POST передает данные, используя тело HTTP запроса. Длина URL ограничена 1024 символами, это и будет верхним ограничением для данных, которые можно отослать через GET. POST может отправлять гораздо большие объемы данных. Лимит устанавливается web-server и составляет обычно около 2 Mb.Передача данных методом POST более безопасна, чем методом GET, так как секретные данные (например пароль) не отображаются напрямую в web-клиенте пользователя, в отличии от URL, который виден почти всегда. Иногда это преимущество превращается в недостаток - вы не сможете послать данные за кого-то другого.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (451, 'Что такое MIME тип?',
        'MIME, Multipurpose Internet Mail Extension (Многоцелевые расширения Интернет-почты) — спецификация для передачи по сети файлов различного типа: изображений, музыки, текстов, видео, архивов и др. В HTML указание MIME-типа используется при  передаче данных форм и вставки на страницу различных объектов.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (452, 'Что такое Web server?',
        'Web server (Веб-сервер) — сервер, принимающий HTTP-запросы от клиентов и выдающий им HTTP-ответы. Так называют как программное обеспечение, выполняющее функции web-сервера, так и непосредственно компьютер, на котором это программное обеспечение работает.Web-серверы могут иметь различные дополнительные функции, например:  автоматизация работы web-страниц;  ведение журнала обращений пользователей к ресурсам;  аутентификация и авторизация пользователей;  поддержка динамически генерируемых страниц;  поддержка HTTPS для защищённых соединений с клиентами.Наиболее известные web-серверы:  Apache  Microsoft IIS  nginx',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (453, 'Что такое Web application?',
        'Web application (Веб-приложение) - клиент-серверное приложение, в котором клиентом выступает браузер, а сервером — web-сервер. Логика web application распределена между сервером и клиентом, хранение данных осуществляется, преимущественно, на сервере, а обмен информацией происходит по сети. Одним из преимуществ такого подхода является тот факт, что клиенты не зависят от конкретной операционной системы пользователя, поэтому web application является кроссплатформенным сервисом.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (454, 'Что такое Application server?',
        'Application Server (Сервер приложений) — программа, представляющая собой сервер, который занимается системной поддержкой приложений и обеспечивает их жизненный цикл в соответствии с правилами, определёнными в спецификациях. Может работать как полноценный самостоятельный web-сервер или быть поставщиком страниц для другого web-сервера. Обеспечивает обмен данными между приложениями и клиентами, берёт на себя выполнение таких функций, как создание программной среды для функционирующего приложения, идентификацию и авторизацию клиентов, организацию сессии для каждого из них.Наиболее известные серверы приложений Java:  Apache Tomcat  Jetty  JBoss  GlassFish  IBM WebSphere  Oracle Weblogic',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (455, 'Чем отличаются Web server и Application server?',
        'Понятие web server относится скорее к способу передачи данных (конкретно, по протоколу HTTP), в то время как понятие Application server относится к способу выполнения этих самых приложений (конкретно, удаленная обработка запросов клиентов при помощи каких-то программ, размещенных на сервере). Эти понятия нельзя ставить в один ряд. Они обозначают разные признаки программы. Какие-то программы удовлетворяют только одному признаку, какие-то - нескольким сразу.Apache Tomcat умеет выполнять приложения? Да, значит он является application server. Apache Tomcat умеет отдавать данные по HTTP? - Да. Следовательно он является web server.Возьмите какую-нибудь базу данных, в которой на хранимых процедурах описана сложная логика и можно в ответ на SQL-запросы отправлять даже sms. Такую базу данных можно назвать application server, но web server - уже нет, потому что все это не работает с клиентом по HTTP протоколу.Возьмите чистый Apache, в котором не включены никакие модули для поддержки языков программирования. Он умеет отдавать только статичные файлы и картинки по протоколу HTTP. Это web server, но не application server. Включите модуль для поддержки PHP и разместите там программу на PHP, которая делает запросы к базе данных и динамически формирует страницы. Теперь Apache стал и application server.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (456, 'Что такое AJAX? Как принципиально устроена эта технология?',
        'AJAX, Asynchronous Javascript and XML (Асинхронный Javascript и XML) — подход к построению интерактивных пользовательских интерфейсов web-приложений, заключающийся в «фоновом» обмене данными браузера и web-сервера. В результате, при обновлении данных web-страница не перезагружается полностью и web-приложения становятся быстрее и удобнее.При использовании AJAX:1. Пользователь заходит на web-страницу и взаимодействует с каким-нибудь её элементом.2. Скрипт на языке JavaScript определяет, какая информация необходима для обновления страницы.3. Браузер отправляет соответствующий запрос на web-сервер.4. Web-сервер возвращает только ту часть документа, на которую пришёл запрос.5. Скрипт вносит изменения с учётом полученной информации (без полной перезагрузки страницы).AJAX базируется на двух основных принципах:1. использование технологии динамического обращения к серверу «на лету» (без перезагрузки страницы полностью) через динамическое создание:o дочерних фреймов;o тега <script>;o тега <img>.2. использование DHTML для динамического изменения содержания страницы;AJAX не является самостоятельной технологией, это концепция использования нескольких смежных технологий:  (X)HTML, CSS для подачи и стилизации информации;  DOM-модель, операции над которой производятся Javascript на стороне клиента, для обеспечения динамического отображения и взаимодействия с информацией;  XMLHttpRequest или другой транспорт (IFrame, SCRIPT-тег, ...) для асинхронного обмена данными с web-сервером;  JSON или любой другой подходящий формат (форматированный HTML, текст, XML, ...) для обмена данными.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (457, 'Что такое WebSocket?',
        'WebSocket — протокол полнодуплексной связи поверх TCP-соединения, предназначенный для обмена сообщениями между браузером и web-сервером в режиме реального времени.Протокол WebSocket определяет две URI схемы  ws: - нешифрованное соединение  wss: - шифрованное соединение',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (458, 'Что такое JSON?',
        'JSON, JavaScript Object Notation — текстовый формат обмена данными, основанный на JavaScript.JSON представляет собой (в закодированном виде) одну из двух структур:  Набор пар «ключ:значение»;  Упорядоченный набор значений.Ключом может быть только строка (регистрозависимая: имена с буквами в разных регистрах считаются разными).В качестве значений могут быть использованы:  Объект — неупорядоченное множество пар «ключ:значение», заключённое в фигурные скобки { }. Ключ описывается строкой, между ним и значением стоит символ :. Пары ключ-значение отделяются друг от друга запятыми;  Массив (одномерный) — упорядоченное множество значений. Массив заключается в квадратные скобки [ ]. Значения разделяются запятыми.  Число;  Литералы true, false и null;  Строка — упорядоченное множество из нуля или более символов Unicode, заключенное в кавычки " ". Символы могут быть указаны с использованием escape-последовательностей, начинающихся с обратной косой черты \\, или записаны шестнадцатеричным кодом в кодировке UTF-8 в виде \\uFFFF.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (459, 'Что такое JSON схема?',
        'JSON Schema — один из языков описания структуры JSON-документа, используя синтаксис JSON.Это самоописательный язык: при его использовании для обработки данных и описания их допустимости могут использоваться одни и те же инструменты сериализации/десериализации.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (460, 'Что такое cookies?',
        'Сookies («куки») — небольшой фрагмент данных, отправленный web-сервером и хранимый на устройстве пользователя. Всякий раз при попытке открыть страницу сайта, web-клиент пересылает соответствующие этому сайту cookies web-серверу в составе HTTP-запроса. Применяется для сохранения данных на стороне пользователя и на практике обычно используется для:  аутентификации пользователя;  хранения персональных предпочтений и настроек пользователя;  отслеживания состояния сеанса доступа пользователя;  ведения разнообразной статистики.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (461, 'Что такое «сессия»?',
        'Сессия – промежуток времени между первым и последним запросами, которые пользователь отправляет со своего устройства на сервер сайта. Завершается сессия в случае, если со стороны пользователя не поступало запросов в течение определенного промежутка времени или же при обрыве связи.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (462, 'Что такое «авторизация» и «аутентификация»? Чем они отличаются?',
        'Аутентификация - это проверка соответствия субъекта и того, за кого он пытается себя выдать, с помощью некой уникальной информации (отпечатки пальцев, цвет радужки, голос и тд.), в простейшем случае - с помощью имени входа и пароля.Авторизация - это проверка и определение полномочий на выполнение некоторых действий (например, чтение файла) в соответствии с ранее выполненной аутентификацией.Очевидно, что это разные понятия, но при этом без первого не может быть второго и наоборот. То есть имея разрешение на работу, вы не сможете оказаться на рабочем месте без предъявления пропуска, равно как и нет смысла в демонстрации пропуска, если вы не планируете работать. Именно тот факт, что одного не бывает без другого, и вызывает у людей заблуждение, что это одно и то же.',
        8);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (463, '﻿Что такое «сервлет»?',
        'Сервлет является интерфейсом, реализация которого расширяет функциональные возможности сервера. Сервлет взаимодействует с клиентами посредством принципа запрос-ответ. Хотя сервлеты могут обслуживать любые запросы, они обычно используются для расширения веб-серверов.Большинство необходимых для создания сервлетов классов и интерфейсов содержатся в пакетах javax.servlet и javax.servlet.http.Основные методы сервлета:  public void init(ServletConfig config) throws ServletException запускается сразу после загрузки сервлета в память;  public ServletConfig getServletConfig() возвращает ссылку на объект, который предоставляет доступ к информации о конфигурации сервлета;  public String getServletInfo() возвращает строку, содержащую информацию о сервлете, например: автор и версия сервлета;  public void service(ServletRequest request, ServletResponse response) throws ServletException, java.io.IOException вызывается для обработки каждого запроса;  public void destroy() выполняется перед выгрузкой сервлета из памяти.Текущая спецификация - Servlet 3.1 описана в JSR-340 и принята в 2013 году.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (464, 'В чем заключаются преимущества технологии сервлетов над CGI (Common Gateway Interface)?',
        '  Сервлеты предоставляют лучшую производительность обработки запросов и более эффективное использование памяти за счет использования преимущество многопоточности (на каждый запрос создается новая нить, что быстрее выделения памяти под новый объект для каждого запроса, как это происходит в CGI).  Сервлеты, как платформа и система являются независимыми. Таким образом веб-приложение, написанное с использованием сервлетов может быть запущена в любом контейнере сервлетов, реализующим этот стандарт и в любой операционной системе.  Использование сервлетов повышает надежность программы, т.к. контейнер сервлетов самостоятельно заботится о жизненном цикле сервлетов (а значит и за утечками памяти), безопасности и сборщике мусора.  Сервлеты относительно легки в изучении и поддержке, таким образом разработчику необходимо заботиться только о бизнес-логике приложения, а не внутренней реализации веб-технологий.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (465, 'Какова структура веб-проекта?',
        'src/main/java Исходники приложения/библиотекиsrc/main/resources Ресурсные файлы приложения/библиотекиsrc/main/filters Файлы сервлетных фильтровsrc/main/webapp Исходники веб-приложенияsrc/test/java Исходники тестовsrc/test/resources Ресурсные файлы тестовsrc/test/filters Тесты сервлетных фильтровsrc/it Интеграционные тестыsrc/assembly Описание сборкиsrc/site СайтLICENSE.txt Лицензия проектаNOTICE.txt Замечания и определения библиотек зависимостей.README.txt Описание проекта',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (466, 'Что такое «контейнер сервлетов»?',
        'Контейнер сервлетов — программа, представляющая собой сервер, который занимается системной поддержкой сервлетов и обеспечивает их жизненный цикл в соответствии с правилами, определёнными в спецификациях. Может работать как полноценный самостоятельный веб-сервер, быть поставщиком страниц для другого веб-сервера, или интегрироваться в Java EE сервер приложений.Контейнер сервлетов обеспечивает обмен данными между сервлетом и клиентами, берёт на себя выполнение таких функций, как создание программной среды для функционирующего сервлета, идентификацию и авторизацию клиентов, организацию сессии для каждого из них.Наиболее известные реализации контейнеров сервлетов:  Apache Tomcat  Jetty  JBoss  WildFly  GlassFish  IBM WebSphere  Oracle Weblogic',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (467, 'Зачем нужны сервера приложений, если есть контейнеры сервлетов?',
        '  Пулы соединений с БДo Возможность периодического тестирования доступности СУБД и обновления соединения в случае восстановления после сбоевo Замена прав доступа при подключенииo Балансировка нагрузки между несколькими СУБД, определение доступность или недоступность того или иного узлаo Защита пула соединений от некорректного кода в приложении, которое по недосмотру не возвращает соединения, просто отбирая его после какого-то таймаута.  JMSo Доступность сервера очередей сообщений "из-коробки".o Возможность кластеризации очередей, т.е. доступность построения распределенных очередей, расположенных сразу на нескольких серверах, что существенно увеличивает масштабируемость и доступность приложенияo Возможность миграции очередей - в случае падения одного из серверов, его очереди автоматически перемещаются на другой, сохраняя необработанные сообщения.o В некоторых серверах приложений поддерживается Unit-of-Order - гарантированный порядок обработки сообщений, удовлетворяющих некоторым критериям.  JTA Встроенная поддержка распределенных транзакций для обеспечения согласованности данных в разные СУБД или очереди.  Безопасностьo Наличие множества провайдеров безопасности и аутентификации:§ во встроенном или внешнем LDAP-сервере§ в базе данных§ в различных Internet-directory (специализированных приложениях для управления правами доступа)o Доступность Single-Sign-On (возможности разделения пользовательской сессии между приложениями) посредством Security Assertion Markup Language (SAML) 1/2 или Simple and Protected Negotiate (SPNEGO) и Kerberos: один из серверов выступает в роли базы для хранения пользователей, все другие сервера при аутентификации пользователя обращаются к этой базе.o Возможность авторизации посредством протокола eXtensible Access Control Markup Language (XACML), позволяющего описывать довольно сложные политики (например, приложение доступно пользователю только в рабочее время).o Кластеризация всего вышеперечисленного  Масштабируемость и высокая доступность Для контейнера сервлетов обычно так же возможно настроить кластеризацию, но она будет довольно примитивной, так как в случае его использования имеются следующие ограничения:o Сложность передачи пользовательской сессии из одного центра обработки данных (ЦоД) в другой через Интернетo Отсутствие возможности эффективно настроить репликации сессий на большом (состоящем из 40-50 экземпляров серверов) кластереo Невозможность обеспечения миграции экземпляров приложения на другой серверo Недоступность механизмов автоматического мониторинга и реакции на ошибки  Управляемостьo Присутствие единого центра управления, т.н. AdminServer и аналога NodeManager’а, обеспечивающего§ Возможность одновременного запуска нескольких экземпляров сервера§ Просмотр состояния запущенных экземпляров сервера, обработчиков той или иной очереди, на том или ином сервере, количества соединений с той или иной БД  Административный канал и развертывание в промышленном режиме Некоторые сервера приложений позволяют включить так называемый "административный канал" - отдельный порт, запросы по которому имеют приоритет.o Просмотр состояния (выполняющихся транзакций, потоков, очередей) в случае недоступности ("зависания") сервераo Обновление приложений "на-лету", без простоя:§ добавление на сервер новой версии приложения в "закрытом" режиме, пока пользователи продолжают работать со предыдущей§ тестирование корректности развертывания новой версии§ "скрытый" перевод на использование новой версии всех пользователей',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (468, 'Как контейнер сервлетов управляет жизненным циклом сервлета, когда и какие методы вызываются?',
        'Контейнер сервлетов управляет четырьмя фазами жизненного цикла сервлета:  Загрузка класса сервлета — когда контейнер получает запрос для сервлета, то происходит загрузка класса сервлета в память и вызов его конструктора без параметров.  Инициализация класса сервлета — после того как класс загружен контейнер инициализирует объект ServletConfig для этого сервлета и внедряет его через init() метод. Это и есть место где сервлет класс преобразуется из обычного класса в сервлет.  Обработка запросов — после инициализации сервлет готов к обработке запросов. Для каждого запроса клиента сервлет контейнер порождает новый поток и вызывает метод service() путем передачи ссылки на объекты ответа и запроса.  Удаление - когда контейнер останавливается или останавливается приложение, то контейнер сервлетов уничтожает классы сервлетов путем вызова destroy() метода.Таким образом, сервлет создаётся при первом обращении к нему и живёт на протяжении всего времени работы приложения (в отличии от объектов классов, которые уничтожаются сборщиком мусора после того, как они уже не используются) и весь жизненный цикл сервлета можно описать как последовательность вызова методов:  public void init(ServletConfig config) – используется контейнером для инициализации сервлета. Вызывается один раз за время жизни сервлета.  public void service(ServletRequest request, ServletResponse response) – вызывается для каждого запроса. Метод не может быть вызван раньше выполнения init() метода.  public void destroy() – вызывается для уничтожения сервлета (один раз за время жизни сервлета).',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (469, 'Что такое «дескриптор развертывания»?',
        'Дескриптор развертывания — это конфигурационный файл артефакта, который будет развернут в контейнере сервлетов. В спецификации Java Platform, Enterprise Edition дескриптор развертывания описывает то, как компонент, модуль или приложение (такое, как веб-приложение или приложение предприятия) должно быть развернуто.Этот конфигурационный файл указывает параметры развертывания для модуля или приложения с определенными настройками, параметры безопасности и описывает конкретные требования к конфигурации. Для синтаксиса файлов дескриптора развертывания используется язык XML.Для веб-приложений дескриптор развертывания должен называться web.xml и находиться в директории WEB-INF, в корне веб-приложения. Этот файл является стандартным дескриптором развертывания, определенным в спецификации. Также есть и другие типы дескрипторов, такие, как файл дескриптора развертывания sun-web.xml, содержащий специфичные для Sun GlassFish Enterprise Server данные для развертывания именно для этого сервера приложений или файл application.xml в директории META-INF для приложений J2EE.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (470, 'Какие действия необходимо проделать при создании сервлетов?',
        'Чтобы создать сервлет ExampleServlet, необходимо описать его в дескрипторе развёртывания:Затем создать класс xyz.company.ExampleServlet путём наследования от HttpServlet и реализовать логику его работы в методе service() или методах doGet()/doPost().',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (471, 'В каком случае требуется переопределять метод service()?',
        'Метод service() переопределяется, когда необходимо, чтобы сервлет обрабатывал все запросы (и GET, и POST) в одном методе.Когда контейнер сервлетов получает запрос клиента, то происходит вызов метода service(), который в зависимости от поступившего запроса вызывает или метод doGet() или метод doPost().',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (472, 'Есть ли смысл определять для сервлета конструктор? Каким образом лучше инициализировать данные?',
        'Большого смысла определять для сервлета конструктор нет, т.к. инициализировать данные лучше не в конструкторе, а переопределив метод init(), в котором имеется возможность доступа к параметрам инициализации сервлета через использование объекта ServletConfig.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (473, 'Почему необходимо переопределить только init() метод без аргументов?',
        'Метод init() переопределяется, если необходимо инициализировать какие-то данные до того как сервлет начнет обрабатывать запросы.При переопределении метода init(ServletConfig config), первым должен быть вызван метод super(config), который обеспечит вызов метода init(ServletConfig config) суперкласса. GenericServlet предоставляет другой метод init() без параметров, который будет вызываться в конце метода init(ServletConfig config).Необходимо использовать переопределенный метод init() без параметров для инициализации данных во избежание каких-либо проблем, например ошибку, когда вызов super() не указан в переопределенном init(ServletConfig config).',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (474, 'Какие наиболее распространенные задачи выполняются в контейнере сервлетов?',
        '  Поддержка обмена данными. Контейнер сервлетов предоставляет легкий способ обмена данными между веб клиентом (браузером) и сервлетом. Благодаря контейнеру нет необходимости создавать слушателя сокета на сервере для отслеживания запросов от клиента, а также разбирать запрос и генерировать ответ. Все эти важные и комплексные задачи решаются с помощью контейнера и разработчик может сосредоточиться на бизнес логике приложения.  Управление жизненным циклом сервлетов и ресурсов. Начиная от загрузки сервлета в память, инициализации, внедрения методов и заканчивая уничтожением сервлета. Контейнер так же предоставляет дополнительные утилиты, например JNDI, для управления пулом ресурсов.  Поддержка многопоточности. Контейнер самостоятельно создает новую нить для каждого запроса и предоставляет ей запрос и ответ для обработки. Таким образом сервлет не инициализируется заново для каждого запроса и тем самым сохраняет память и уменьшает время до обработки запроса.  Поддержка JSP. JSP классы не похожи на стандартные классы джавы, но контейнер сервлетов преобразует каждую JSP в сервлет и далее управляется контейнером как обычным сервлетом.  Различные задачи. Контейнер сервлетов управляет пулом ресурсов, памятью приложения, сборщиком мусора. Предоставляются возможности настройки безопасности и многое другое.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (475, 'Что вы знаете о сервлетных фильтрах?',
        'Сервлетный фильтр - это Java-код, пригодный для повторного использования и позволяющий преобразовать содержание HTTP-запросов, HTTP-ответов и информацию, содержащуюся в заголовках HTML. Сервлетный фильтр занимается предварительной обработкой запроса, прежде чем тот попадает в сервлет, и/или последующей обработкой ответа, исходящего из сервлета.Сервлетные фильтры могут:  перехватывать инициацию сервлета прежде, чем сервлет будет инициирован;  определить содержание запроса прежде, чем сервлет будет инициирован;  модифицировать заголовки и данные запроса, в которые упаковывается поступающий запрос;  модифицировать заголовки и данные ответа, в которые упаковывается получаемый ответ;  перехватывать инициацию сервлета после обращения к сервлету.Сервлетный фильтр может быть конфигурирован так, что он будет работать с одним сервлетом или группой сервлетов. Основой для формирования фильтров служит интерфейс javax.servlet.Filter, который реализует три метода:  void init(FilterConfig config) throws ServletException;  void destroy();  void doFilter(ServletRequest request, ServletResponse response, FilterChain chain) throws IOException, ServletException;Метод init() вызывается прежде, чем фильтр начинает работать,и настраивает конфигурационный объект фильтра. Метод doFilter() выполняет непосредственно работу фильтра. Таким образом, сервер вызывает init() один раз, чтобы запустить фильтр в работу, а затем вызывает doFilter() столько раз, сколько запросов будет сделано непосредственно к данному фильтру. После того, как фильтр заканчивает свою работу, вызывается метод destroy().Интерфейс FilterConfig содержит метод для получения имени фильтра, его параметров инициации и контекста активного в данный момент сервлета. С помощью своего метода doFilter() каждый фильтр получает текущий запрос request и ответ response, а также FilterChain, содержащий список фильтров, предназначенных для обработки. В doFilter() фильтр может делать с запросом и ответом всё, что ему захочется - собирать данные или упаковывать объекты для придания им нового поведения. Затем фильтр вызывает chain.doFilter(), чтобы передать управление следующему фильтру. После возвращения этого вызова фильтр может по окончании работы своего метода doFilter() выполнить дополнительную работу над полученным ответом. К примеру, сохранить регистрационную информацию об этом ответе.После того, как класс-фильтр откомпилирован, его необходимо установить в контейнер и «приписать» (map) к одному или нескольким сервлетам. Объявление и подключение фильтра отмечается в дескрипторе развёртывания web.xml внутри элементов <filter> и <filter-mapping>. Для подключение фильтра к сервлету необходимо использовать вложенные элементы <filter-name> и <servlet-name>.Порядок, в котором контейнер строит цепочку фильтров для запроса определяется следующими правилами:  цепочка, определяемая <url-pattern>, выстраивается в том порядке, в котором встречаются соответствующие описания фильтров в web.xml;  последовательность сервлетов, определенных с помощью <servlet-name>, также выполняется в той последовательности, в какой эти элементы встречаются в дескрипторе развёртывания web.xml.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (476, 'Зачем в сервлетах используются различные listener?',
        'Listener (слушатель) работает как триггер, выполняя определённые действия при наступлении какого-либо события в жизненном цикле сервлета.Слушатели, разделённые по области видимости (scope):  Request:o ServletRequestListener используется для того, чтобы поймать момент создания и уничтожения запроса;o ServletRequestAttributeListener используется для прослушивания событий, происходящих с атрибутами запроса.  Context:o ServletContextListener позволяет поймать момент, когда контекст инициализируется либо уничтожается;o ServletContextAttributeListener используется для прослушивании событий, происходящих с атрибутами в контексте.  Session:o HttpSessionListener позволяет поймать момент создания и уничтожения сессии;o HttpSessionAttributeListener используется при прослушивании событий происходящих с атрибутами в сессии;o HttpSessionActivationListener используется в случае, если происходит миграция сессии между различными JVM в распределённых приложениях;o HttpSessionBindingListener так же используется для прослушивания событий, происходящих с атрибутами в сессии. Разница между HttpSessionAttributeListener и HttpSessionBindingListener слушателями: первый декларируется в web.xml; экземпляр класса создается контейнером автоматически в единственном числе и применяется ко всем сессиям; второй: экземпляр класса должен быть создан и закреплён за определённой сессией «вручную», количество экземпляров также регулируется самостоятельно.HttpSessionBindingListener подключается в качестве атрибута непосредственно в сессию, т.е., чтобы его подключить необходимо:  создать экземпляр класса, реализующего этот интерфейс;  положить созданный экземпляр в сессию при помощи setAttribute(String, Object).',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (477, 'Когда стоит использовать фильтры сервлетов, а когда слушателей?',
        'Следует использовать фильтры, если необходимо обрабатывать входящие или исходящие данные (например: для аутентификации, преобразования формата, компрессии, шифрования и т.д.), в случае, когда необходимо реагировать на события - лучше применять слушателей.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (478, 'Как реализовать запуск сервлета одновременно с запуском приложения?',
        'Контейнер сервлетов обычно загружает сервлет по первому запросу клиента.Если необходимо загрузить сервлет прямо на старте приложения (например если загрузка сервлета происходит длительное время) следует использовать элемент <load-on-startup> в дескрипторе или аннотацию @loadOnStartup в коде сервлета, что будет указывать на необходимость загрузки сервлета при запуске.Если целочисленное значение этого параметра отрицательно, то сервлет будет загружен при запросе клиента. В противном случае - загрузится на старте приложения, при этом, чем число меньше, тем раньше в очереди на загрузку он окажется.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (479, 'Как обработать в приложении исключения, выброшенные другим сервлетом?',
        'Когда приложение выбрасывет исключение контейнер сервлетов обрабатывает его и создаёт ответ в формате HTML. Это аналогично тому, что происходит при кодах ошибок вроде 404, 403 и т.д.В дополнении к этому существует возможность написания собственных сервлетов для обработки исключений и ошибок с указанием их в дескрипторе развертывания:Основная задача таких сервлетов - обработать ошибку/исключение и сформировать понятный ответ пользователю. Например, предоставить ссылку на главную страницу или же описание ошибки.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (480, 'Что представляет собой ServletConfig?',
        'Интерфейс javax.servlet.ServletConfig используется для передачи сервлету конфигурационной информации. Каждый сервлет имеет свой собственный экземпляр объекта ServletConfig, создаваемый контейнером сервлетов.Для получения ServletConfig сервлета используется метод getServletConfig().',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (481, 'Что представляет собой ServletContext?',
        'Уникальный (в рамках веб-приложения) объект ServletContext реализует интерфейс javax.servlet.ServletContext и предоставляет сервлетам доступ к параметрам этого веб-приложения.  Для предоставления доступа используется элемент <context-param> в web.xml:<web-app>    ...    <context-param>        <param-name>exampleParameter</param-name>        <param-value>parameterValue</param-value>    </context-param>    ...</web-app>Объект ServletContext можно получить с помощью метода getServletContext() у интерфейса ServletConfig. Контейнеры сервлетов так же могут предоставлять контекстные объекты, уникальные для группы сервлетов. Каждая из групп будет связана со своим набором URL-путей хоста. В спецификации Servlet 3 ServletContext был расширен и теперь предоставляет возможности программного добавления слушателей и фильтров в приложение. Так же у этого интерфейса имеется множество полезных методов таких как 	getServerInfo(), getMimeType(), getResourceAsStream() и т.д.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (482, 'В чем отличия ServletContext и ServletConfig?',
        '  ServletConfig уникален для сервлета, а ServletContext - для приложения;  ServletConfig используется для предоставления параметров инициализации конкретному сервлету, а ServletContext для предоставления параметров инициализации для всех сервлетов приложения;  для ServletConfig возможности модифицировать атрибуты отсутствуют, атрибуты в объекте ServletContext можно изменять.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (483, 'Для чего нужен интерфейс ServletResponse?',
        'Интерфейс ServletResponse используется для отправки данных клиенту. Все методы данного инструмента служат именно этой цели:  String getCharacterEncoding() - возвращает MIME тип кодировки (к примеру - UTF8), в которой будет выдаваться информация;  void setLocale(Locale locale)/Locale getLocale() - указывают на язык используемый в документе;  ServletOutputStream getOutputStream()/PrintWriter getWriter() - возвращают потоки вывода данных;  void setContentLength(int len) - устанавливает значение поля HTTP заголовка Content-Length;  void setContentType(String type) - устанавливает значение поля HTTP заголовка Content-Type.  void reset() - позволяет сбросить HTTP заголовок к значениям по-умолчанию, если он ещё не был отправлен  и др.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (484, 'Для чего нужен интерфейс ServletRequest?',
        'Интерфейс ServletRequest используется для получения параметров соединения, запроса, а также заголовков, входящего потока данных и т.д.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (485, 'Что такое Request Dispatcher?',
        'Интерфейс RequestDispatcher используется для передачи запроса другому ресурсу, при этом существует возможность добавления данных, полученных из этого ресурса к собственному ответу сервлета. Так же этот интерфейс используется для внутренней коммуникации между сервлетами в одном контексте.В интерфейсе объявлено два метода:  void forward(ServletRequest var1, ServletResponse var2) — передает запрос из сервлета к другому ресурсу (сервлету, JSP или HTML файлу) на сервере.  void include(ServletRequest var1, ServletResponse var2) — включает контент ресурса (сервлет, JSP или HTML страница) в ответ.Доступ к интерфейсу можно получить с помощью метода интерфейса ServletContext - RequestDispatcher getRequestDispatcher(String path), где путь начинающийся с /, интерпретируется относительно текущего корневого пути контекста.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (486, 'Как из одного сервлета вызвать другой сервлет?',
        'Для вызова сервлета из того же приложения необходимо использовать механизм внутренней коммуникации сервлетов (inter-servlet communication mechanisms) через вызовы методов RequestDispatcher:  forward() - передаёт выполнение запроса в другой сервлет;  include() - предоставляет возможность включить результат работы другого сервлета в возвращаемый ответ.Если необходимо вызывать сервлет принадлежащий другому приложению, то использовать RequestDispatcher уже не получится, т.к. он определен только для текущего приложения. Для подобных целей необходимо использовать метод ServletResponse - sendRedirect() которому предоставляется полный URL другого сервлета. Для передачи данных между сервлетами можно использовать cookies.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (487, 'Чем отличается sendRedirect() от forward()?',
        'forward():  Выполняется на стороне сервера;  Запрос перенаправляется на другой ресурс в пределах того же сервера;  Не зависит от протокола клиентского запроса, так как обеспечивается контейнером сервлетов;  Нельзя применять для внедрения сервлета в другой контекст;  Клиент не знает о фактически обрабатываемом ресурсе и URL в строке остается прежним;  Выполняется быстрее метода sendRedirect();  Определён в интерфейсе RequestDispatcher.sendRedirect():  Выполняется на стороне клиента;  Клиенту возвращается ответ 302 (redirect) и запрос перенаправляется на другой сервер;  Может использоваться только с клиентами HTTP;  Разрешается применять для внедрения сервлета в другой контекст;  URL адрес изменяется на адрес нового ресурса;  Медленнее forward() т.к. требует создания нового запроса;  Определён в интерфейсе HttpServletResponse.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (488, 'Для чего используются атрибуты сервлетов и как происходит работа с ними?',
        'Атрибуты сервлетов используются для внутренней коммуникации сервлетов.В веб-приложении существует возможность работы с атрибутами используя методы setAttribute(), getAttribute(), removeAttribute(), getAttributeNames(), которые предоставлены интерфейсами ServletRequest, HttpSession и ServletContext (для областей видимости request, session, context соответственно).',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (489, 'Каким образом можно допустить в сервлете deadlock?',
        'Можно получить блокировку, например, допустив циклические вызовы метода doPost() в методе doGet() и метода doGet() в методе doPost().',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (490, 'Как получить реальное расположение сервлета на сервере?',
        'Реальный путь к расположению сервлета на сервере можно получить из объекта ServletContext:getServletContext().getRealPath(request.getServletPath()).',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (491, 'Как получить информацию о сервере из сервлета?',
        'Информацию о сервере можно получить из объекта ServletContext:getServletContext().getServerInfo().', 9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (492, 'Как получить IP адрес клиента на сервере?',
        'IP адрес клиента можно получить вызвав request.getRemoteAddr().', 9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (493, 'Какие классы-обертки для сервлетов вы знаете?',
        'Собственные обработчики ServletRequest и ServletResponse можно реализовать, добавив новые или переопределив существующие методы у классов-обёрток ServletRequestWrapper (HttpServletRequestWrapper) и ServletResponseWrapper (HttpServletRequestWrapper).',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (494, 'В чем отличия GenericServlet и HttpServlet?',
        'Абстрактный класс GenericServlet — независимая от используемого протокола реализация интерфейса Servlet, а абстрактный класс HttpServlet в свою очередь расширяет GenericServlet для протокола HTTP..',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (495, 'Почему HttpServlet класс объявлен как абстрактный?',
        'Класс HTTPServlet предоставляет лишь общую реализацию сервлета для HTTP протокола. Реализация ключевых методов doGet() и doPost(), содержащих основную бизнес-логику перекладывается на разработчика и по умолчанию возвращает HTTP 405 Method Not Implemented error.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (496, 'Какие основные методы присутствуют в классе HttpServlet?',
        '  doGet() - для обработки HTTP запросов GET;  doPost() - для обработки HTTP запросов POST;  doPut() - для обработки HTTP запросов PUT;  doDelete() - для обработки HTTP запросов DELETE;  doHead() - для обработки HTTP запросов HEAD;  doOptions() - для обработки HTTP запросов OPTIONS;  doTrace() - для обработки HTTP запросов TRACE.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (497, 'Стоит ли волноваться о многопоточной безопасности работая с сервлетами?',
        'Методы init() и destroy() вызываются один раз за жизненный цикл сервлета — поэтому по поводу них беспокоиться не стоит.Методы doGet(), doPost(), service() вызываются на каждый запрос клиента и т.к. сервлеты используют многопоточность, то здесь задумываться о потокобезопасной работе обязательно. При этом правила использования многопоточности остаются теми же: локальные переменные этих методов будут созданы отдельно для каждого потока, а при использовании глобальных разделяемых ресурсов необходимо использовать синхронизацию или другие приёмы многопоточного программирования.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (498, 'Какой метод HTTP не является неизменяемым?',
        'HTTP метод называется неизменяемым, если он на один и тот же запрос всегда возвращает одинаковый результат. HTTP методы GET, PUT, DELETE, HEAD и OPTIONS являются неизменяемыми, поэтому необходимо реализовывать приложение так, чтобы эти методы возвращали одинаковый результат постоянно. К изменяемым методам относится метод POST, который и используется для реализации чего-либо, что изменяется при каждом запросе.К примеру, для доступа к статической HTML странице используется метод GET, т.к. он всегда возвращает одинаковый результат. При необходимости сохранять какую-либо информацию, например в базе данных, нужно использовать POST метод.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (499, 'Какие есть методы отправки данных с клиента на сервер?',
        '  GET - используется для запроса содержимого указанного ресурса, изображения или гипертекстового документа. Вместе с запросом могут передаваться дополнительные параметры как часть URI, значения могут выбираться из полей формы или передаваться непосредственно через URL. При этом запросы кэшируются и имеют ограничения на размер. Этот метод является основным методом взаимодействия браузера клиента и веб-сервера.  POST - используется для передачи пользовательских данных в содержимом HTTP-запроса на сервер. Пользовательские данные упакованы в тело запроса согласно полю заголовка Content-Type и/или включены в URI запроса. При использовании метода POST под URI подразумевается ресурс, который будет обрабатывать запрос.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (500, 'В чем разница между методами GET и POST?',
        '  GET передает данные серверу используя URL, тогда как POST передает данные, используя тело HTTP запроса. Длина URL ограничена 1024 символами, это и будет верхним ограничением для данных, которые можно отослать через GET. POST может отправлять гораздо большие объемы данных. Лимит устанавливается web-server и составляет обычно около 2 Mb.  Передача данных методом POST более безопасна, чем методом GET, так как секретные данные (например пароль) не отображаются напрямую в web-клиенте пользователя, в отличии от URL, который виден почти всегда. Иногда это преимущество превращается в недостаток - вы не сможете послать данные за кого-то другого.  GETметод является неизменяемым, тогда как POST — изменяемый.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (501, 'В чем разница между PrintWriter и ServletOutputStream?',
        'PrintWriter — класс для работы с символьным потоком, экземпляр которого можно получить через метод ServletResponse getWriter();ServletOutputStream — класс для работы байтовым потоком. Для получения его экземпляра используется метод ServletResponse getOutputStream().',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (502, 'Можно ли одновременно использовать в сервлете PrintWriter и ServletOutputStream?',
        'Так сделать не получится, т.к. при попытке одновременного вызова getWriter() и getOutputStream() будет выброшено исключение java.lang.IllegalStateException с сообщением, что уже был вызван другой метод.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (503, 'Расскажите об интерфейсе SingleThreadModel.',
        'Интерфейс SingleThreadModel является маркерным - в нем не объявлен ни один метод, однако, если сервлет реализует этот интерфейс, то метод service() этого сервлета гарантированно не будет одновременно выполняться в двух потоках. Контейнер сервлетов либо синхронизирует обращения к единственному экземпляру, либо обеспечивает поддержку пула экземпляров и перенаправление запроса свободному сервлету.Другими словами, контейнер гарантирует отсутствие конфликтов при одновременном обращении к переменным или методам экземпляра сервлета. Однако существуют также и другие разделяемые ресурсы, которые даже при использовании этого интерфейса, остаются всё так же доступны обработчикам запросов в других потоках. Т.о. пользы от использования этого интерфейса немного и в спецификации Servlet 2.4 он был объявлен deprecated.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (504, 'Что означает URL encoding? Как это осуществить в Java?',
        'URL Encoding — процесс преобразования данных в форму CGI (Common Gateway Interface), не содержащую пробелов и нестандартных символов, которые заменяются в процессе кодирования на специальные escape-символы. В Java для кодирования строки используется метод java.net.URLEncoder.encode(String str, String unicode). Обратная операция декодирования возможна через использование метода java.net.URLDecoder.decode(String str, String unicode).Hello мир! преобразовывается в Hello%20%D0%BC%D0%B8%D1%80!.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (505, 'Какие различные методы управления сессией в сервлетах вы знаете?',
        'При посещении клиентом Web-ресурса и выполнении вариантов запросов, контекстная информация о клиенте не хранится. В протоколе HTTP нет возможностей для сохранения и изменения информации о предыдущих посещениях клиента. Сеанс (сессия) – соединение между клиентом и сервером, устанавливаемое на определенное время, за которое клиент может отправить на сервер сколько угодно запросов. Сеанс устанавливается непосредственно между клиентом и Web-сервером. Каждый клиент устанавливает с сервером свой собственный сеанс. Сеансы используются для обеспечения хранения данных во время нескольких запросов Web-страницы или на обработку информации, введенной в пользовательскую форму в результате нескольких HTTP-соединений (например, клиент совершает несколько покупок в интернет-магазине; студент отвечает на несколько тестов в системе дистанционного обучения).Существует несколько способов обеспечения уникального идентификатора сессии:  User Authentication – Предоставление учетных данных самим пользователем в момент аутентификации. Переданная таким образом информация в дальнейшем используется для поддержания сеанса. Это метод не будет работать, если пользователь вошёл в систему одновременно из нескольких мест.  HTML Hidden Field – Присвоение уникального значения скрытому полю HTML страницы, в момент когда пользователь начинает сеанс. Этот метод не может быть использован со ссылками, потому что нуждается в подтверждении формы со скрытым полем каждый раз во время формирования запроса. Кроме того, это не безопасно, т.к. существует возможность простой подмены такого идентификатора.  URL Rewriting – Добавление идентификатора сеанса как параметра URL. Достаточно утомительная операция, потому что требует постоянного отслеживания этого идентификатора при каждом запросе или ответе.  Cookies – Использование небольших фрагментов данных, отправленных web-сервером и хранимых на устройстве пользователя. Данный метод не будет работать, если клиент отключает использование cookies.  Session Management API – Использование специального API для отслеживания сеанса, построенный на основе и на методах, описанных выше и который решает частные проблемы перечисленных способов:o Чаще всего недостаточно просто отслеживать сессию, необходимо ещё и сохранять какие-либо дополнительные данные о ней, которые могут потребоваться при обработке последующих запросов. Осуществление такого поведения требует много дополнительных усилий.o Все вышеперечисленные методы не являются универсальными: для каждого из них можно подобрать конкретный сценарий, при котором они не будут работать.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (506, 'Что такое cookies?',
        'Сookies («куки») — небольшой фрагмент данных, отправленный web-сервером и хранимый на устройстве пользователя. Всякий раз при попытке открыть страницу сайта, web-клиент пересылает соответствующие этому сайту cookies web-серверу в составе HTTP-запроса. Применяется для сохранения данных на стороне пользователя и на практике обычно используется для:  аутентификации пользователя;  хранения персональных предпочтений и настроек пользователя;  отслеживания состояния сеанса доступа пользователя;  ведения разнообразной статистики.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (507, 'Какие методы для работы с cookies предусмотрены в сервлетах?',
        'Servlet API предоставляет поддержку cookies через класс javax.servlet.http.Cookie:  Для получения массива cookies из запроса необходимо воспользоваться методом HttpServletRequest.getCookies(). Методов для добавления cookies в HttpServletRequest не предусмотрено.  Для добавления cookie в ответ используется HttpServletResponse.addCookie(Cookie c). Метода получения cookies в HttpServletResponse отсутствует.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (508, 'Что такое URL Rewriting?',
        'URL Rewriting - специальная перезапись (перекодирование) оригинального URL. Данный механизм может использоваться для управления сессией в сервлетах, когда cookies отключены.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (509, 'Зачем нужны и чем отличаются методы encodeURL() и encodeRedirectURL()?',
        'HttpServletResponse.encodeURL() предоставляет способ преобразования URL в HTML гиперссылку с преобразованием спецсимволов и пробелов, а так же добавления session id к URL. Такое поведение аналогично java.net.URLEncoder.encode(), но с добавлением дополнительного параметра jsessionid в конец URL.Метод HttpServletResponse.encodeRedirectURL() преобразует URL для последующего использования в методе sendRedirect().Таким образом для HTML гиперссылок при URL rewriting необходимо использовать encodeURL(), а для URL при перенаправлении - encodeRedirectUrl().',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (510, 'Что такое «сессия»?',
        'Сессия  - это сеанс связи между клиентом и сервером, устанавливаемый на определенное время. Сеанс устанавливается непосредственно между клиентом и веб-сервером в момент получения первого запроса к веб-приложению. Каждый клиент устанавливает с сервером свой собственный сеанс, который сохраняется до окончания работы с приложением.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (511, 'Как уведомить объект в сессии, что сессия недействительна или закончилась?',
        'Чтобы быть уверенным в том, что объект будет оповещён о прекращении сессии, нужно реализовать интерфейс javax.servlet.http.HttpSessionBindingListener. Два метода этого интерфейса: valueBound() и valueUnbound() используются при добавлении объекта в качестве атрибута к сессии и при уничтожении сессии соответственно.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (512,
        'Какой существует эффективный способ удостоверится, что все сервлеты доступны только для пользователя с верной сессией?',
        'Сервлет фильтры используются для перехвата всех запросов между контейнером сервлетов и сервлетом. Поэтому логично использовать соответствующий фильтр для проверки необходимой информации (например валидности сессии) в запросе.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (513, 'Как мы можем обеспечить transport layer security для нашего веб приложения?',
        'Для обеспечения transport layer security необходимо настроить поддержку SSL сервлет контейнера. Как это сделать зависит от конкретной реализации сервлет-контейнера.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (514, 'Как организовать подключение к базе данных, обеспечить журналирование в сервлете?',
        'При работе с большим количеством подключений к базе данных рекомендуется инициализировать их в servlet context listener, а также установить в качестве атрибута контекста для возможности использования другими сервлетами.Журналирование подключается к сервлету стандартным для логгера способом (например для log4j это может быть property-файл или XML-конфигурация) , а далее эта информация используется при настройке соответствующего context listener.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (515, 'Какие основные особенности появились в спецификации Servlet 3?',
        '  Servlet Annotations. До Servlet 3 вся конфигурация содержалась в web.xml, что приводило к ошибкам и неудобству при работе с большим количестве сервлетов. Примеры аннотаций: @WebServlet, @WebInitParam, @WebFilter, @WebListener.  Web Fragments. Одностраничное веб приложение может содержать множество модулей: все модули прописываются в fragment.xml в папке META-INF\\. Это позволяет разделять веб приложение на отдельные модули, собранные как .jar-файлы в отдельной lib\\ директории.  Динамическое добавление веб компонентов. Появилась возможность программно добавлять фильтры и слушатели, используя ServletContext объект. Для этого применяются методы addServlet(), addFilter(), addListener(). Используя это нововведение стало доступным построение динамической системы, в которой необходимый объект будет создан и вызван только по необходимости.  Асинхронное выполнение. Поддержка асинхронной обработки позволяет передать выполнение запроса в другой поток без удержания всего сервера занятым.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (516, 'Какие способы аутентификации доступны сервлету?',
        'Спецификация сервлетов определяет четыре типа проверки подлинности:  HTTP Basic Authentication - BASIC. При доступе к закрытым ресурсам появится окно, которое попросит ввести данные для аутентификации.  Form Based Login - FORM. Используется собственная html форма:  HTTP Digest Authentication - DIGEST. Цифровая аутентификация с шифрованием.  HTTPS Authentication - CLIENT-CERT. Аутентификация с помощью клиентского сертификата.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (517, 'Что такое Java Server Pages (JSP)?',
        'JSP (JavaServer Pages) — платформонезависимая переносимая и легко расширяемая технология разработки веб-приложений, позволяющая веб-разработчикам создавать содержимое, которое имеет как статические, так и динамические компоненты. Страница JSP содержит текст двух типов: статические исходные данные, которые могут быть оформлены в одном из текстовых форматов HTML, SVG, WML, или XML, и JSP-элементы, которые конструируют динамическое содержимое. Кроме этого могут использоваться библиотеки JSP-тегов, а также EL (Expression Language), для внедрения Java-кода в статичное содержимое JSP-страниц.Код JSP-страницы транслируется в Java-код сервлета с помощью компилятора JSP-страниц Jasper, и затем компилируется в байт-код JVM.JSP-страницы загружаются на сервере и управляются Java EE Web Application. Обычно такие страницы упакованы в файловые архивы .war и .ear.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (518, 'Зачем нужен JSP?',
        'JSP расширяет технологию сервлетов обеспечивая возможность создания динамических страницы с HTML подобным синтаксисом.Хотя создание представлений поддерживается и в сервлетах, но большая часть любой веб-страницы является статической, поэтому код сервлета в таком случае получается чересчур перегруженным, замусоренным и поэтому при его написании легко допустить ошибку.Еще одним преимуществом JSP является горячее развертывание - возможность заменить одну страницу на другую непосредственно в контейнере без необходимости перекомпилировать весь проект или перезапускать сервер.Однако рекомендуется избегать написания серьёзной бизнес-логики в JSP и использовать страницу только в качестве представления.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (519, 'Опишите, как обрабатываются JSP страницы, начиная от запроса к серверу, заканчивая ответом пользователю.',
        'Когда пользователь переходит по ссылке на страницу page.jsp, он отправляет http-запрос на сервер GET /page.jsp. Затем, на основе этого запроса и текста самой страницы, сервер генерирует java-класс, компилирует его и выполняет полученный сервлет, формирующий ответ пользователю в виде представления этой страницы, который сервер и перенаправляет обратно пользователю.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (520, 'Расскажите об этапах (фазах) жизненного цикла JSP.',
        'Если посмотреть код внутри созданной JSP страницы, то он будет выглядеть как HTML и не будет похож на java класс. Конвертацией JSP страниц в HTML код занимается контейнер, который так же создает и сервлет для использования в веб приложении.Жизненный цикл JSP состоит из нескольких фаз, которыми руководит JSP контейнер:  Translation – проверка и парсинг кода JSP страницы для создания кода сервлета.  Compilation – компиляция исходного кода сервлета.  Class Loading – загрузка скомпилированного класса в память.  Instantiation – внедрение конструктора без параметра загруженного класса для инициализации в памяти.  Initialization – вызов init() метода объекта JSP класса и инициализация конфигурации сервлета с первоначальными параметрами, которые указаны в дескрипторе развертывания (web.xml). После этой фазы JSP способен обрабатывать запросы клиентов. Обычно эти фазы происходят после первого запроса клиента (т.е. ленивая загрузка), но можно настроить загрузку и инициализацию JSP на старте приложения по аналогии с сервлетами.  Request Processing – длительный жизненный цикл обработки запросов клиента JSP страницей. Обработка является многопоточной и аналогична сервлетам — для каждого запроса создается новый поток, объекты ServletRequest и ServletResponse, происходит выполнение сервис методов.  Destroy – последняя фаза жизненного цикла JSP, на которой её класс удаляется из памяти. Обычно это происходит при выключении сервера или выгрузке приложения.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (521, 'Расскажите о методах жизненного цикла JSP.',
        'Контейнер сервлетов (например, Tomcat, GlassFish) создает из JSP-страницы класс сервлета, наследующего свойства интерфейса javax.servlet.jsp.HttpJspBase и включающего следующие методы:  jspInit() — метод объявлен в JSP странице и реализуется с помощью контейнера. Этот метод вызывается один раз в жизненном цикле JSP для того, чтобы инициализировать конфигурационные параметры указанные в дескрипторе развертывания. Этот метод можно переопределить с помощью определения элемента JSP scripting и указания необходимых параметров для инициализации;  _jspService() — метод переопределяется контейнером автоматически и соответствует непосредственно коду JSP, описанному на странице. Этот метод определен в интерфейсе HttpJspPage, его имя начинается с нижнего подчеркивания и он отличается от других методов жизненного цикла тем, что его невозможно переопределить;  jspDestroy() — метод вызывается контейнером для удаления объекта из памяти (на последней фазе жизненного цикла JSP - Destroy). Метод вызывается только один раз и доступен для переопределения, предоставляя возможность освободить ресурсы, которые были созданы в jspInit().',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (522, 'Какие методы жизненного цикла JSP могут быть переопределены?',
        'Возможно переопределить лишь jspInit() и jspDestroy() методы.', 9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (523, 'Как можно предотвратить прямой доступ к JSP странице из браузера?',
        'Прямой доступ к директории /WEB-INF/ из веб-приложения отсутствует. Поэтому JSP-страницы можно расположить внутри этой папки и тем самым запретить доступ к странице из браузера. Однако, по аналогии с описанием сервлетов, будет необходимо настроить дескриптор развертывания:',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (524, 'Какая разница между динамическим и статическим содержимым JSP?',
        'Статическое содержимое JSP (HTML, код JavaScript, изображения и т.д.) не изменяется в процессе работы веб приложения.Динамические ресурсы созданы для того, чтобы отображать свое содержимое в зависимости от пользовательских действий. Обычно они представлены в виде выражений EL (Expression Language), библиотек JSP-тегов и пр.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (525, 'Как закомментировать код в JSP?',
        '  <!—- HTML комментарий; отображается на странице JSP —-> такие комментарии будут видны клиенту при просмотре кода страницы.  <%—- JSP комментарий; не отображается на странице JSP —-%> такие комментарии описываются в созданном сервлете и не посылаются клиенту. Для любых комментариев по коду или отладочной информации необходимо использовать именно такой тип комментариев.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (526, 'Какие существуют основные типы тегов JSP?',
        '  Выражение JSP: <%= expression %> - выражение, которое будет обработано с перенаправлением результата на вывод;  Скриплет JSP: <% code %> - код, добавляемый в метод service().  Объявление JSP: <%! code %> - код, добавляемый в тело класса сервлета вне метода service().  Директива JSP page: <%@ page att="value" %> - директивы для контейнера сервлетов с информацией о параметрах.  Директива JSP include: <%@ include file="url" %> - файл в локальной системе, подключаемый при трансляции JSP в сервлет.  Комментарий JSP: <%-- comment --%> - комментарий; игнорируется при трансляции JSP страницы в сервлет.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (527, 'Что вы знаете о действиях JSP (Action tag и JSP Action Elements).',
        'Action tag и JSP Action Elements предоставляют методы работы с Java Beans, подключения ресурсов, проброса запросов и создания динамических XML элементов. Такие элементы всегда начинаются с записи jsp: и используются непосредственно внутри страницы JSP без необходимости подключения сторонних библиотек или дополнительных настроек.Наиболее часто используемыми JSP Action Elements являются:  jsp:include: <jsp:include page="относительный URL" flush="true"/> - подключить файл при запросе страницы. Если необходимо, чтобы файл подключался в процессе трансляции страницы, то используется директива page совместно с атрибутом include;  jsp:useBean: <jsp:useBean att=значение*/> или <jsp:useBean att=значение*>...</jsp:useBean> - найти или создать Java bean;  jsp:setProperty: <jsp:setProperty att=значение*/> - установить свойства Java bean, или явно, или указанием на соответствующее значение, передаваемое при запросе;  jsp:forward: <jsp:forward page="относительный URL"/> - передать запрос другой странице;  jsp:plugin: <jsp:plugin attribute="значение"*>...</jsp:plugin> - сгенерировать (в зависимости от типа браузера) тэги OBJECT или EMBED для апплета, использующего технологию Java Plugin.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (528, 'Взаимодействие JSP - сервлет - JSP.',
        '«JSP - сервлет - JSP» архитектура построения приложений носит название MVC (Model/View/Controller):  Model - классы данных и бизнес-логики;  View - страницы JSP;  Controller - сервлеты.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (529, 'Какие области видимости переменных существуют в JSP?',
        'Область видимости объектов определяется тем контекстом, в который помещается данный объект. В зависимости от той или иной области действия так же определяется время существования объекта.В JSP предусмотрены следующие области действия переменных (объектов):  request область действия запроса - объект будет доступен на текущей JSP странице, странице пересылки (при использовании jsp:forward) или на включаемой странице (при использовании jsp:include);  session область действия сессии - объект будет помещен в сеанс пользователя, будет доступен на всех JSP страницах и будет существовать пока существует сессия пользователя, или он не будет из нее принудительно удален.  application область действия приложения - объект будет доступен для всех пользователей на всех JSP страницах и будет существовать на протяжении всей работы приложения или пока не будет удален принудительно и контекста приложения.  page область действия страницы - объект будет доступен только на той странице, где он определен. На включаемых (jsp:include) и переадресуемых (jsp:forward) страницах данный объект уже не будет доступен.Таким образом, чтобы объект был доступен всем JSP страницам, необходимо указать область видимости application или session, в зависимости от того требуется ли доступ к объекту всем пользователям или только текущему.Для указания требуемой области действия при определении объекта на JSP странице используется атрибут scope тега jsp:useBean:<jsp:useBean id="myBean" class="ru.javacore.MyBean" scope="session"/>Если не указывать атрибут scope, то по умолчанию задается область видимости страницы page',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (530, 'Какие неявные, внутренние объекты и методы есть на JSP странице?',
        'JSP implicit objects (неявные объекты) создаются контейнером при конвертации JSP страницы в код сервлета для помощи разработчикам. Эти объекты можно использовать напрямую в скриптлетах для передачи информации в сервис методы, однако мы не можем использовать неявные объекты в JSP Declaration, т.к. такой код пойдет на уровень класса.Существует 9 видов неявных объектов, которые можно использовать прямо на JSP странице. Семь из них объявлены как локальные переменные в начале _jspService() метода, а два оставшихся могут быть использованы как аргументы метода _jspService().  out Object :<strong>Current Time is</strong>: <% out.print(new Date()); %><br>  request Object :<strong>Request User-Agent</strong>: <%=request.getHeader("User-Agent") %><br>  response Object :<strong>Response</strong>: <%response.addCookie(new Cookie("Test","Value")); %>  config Object :<strong>User init param value</strong>: <%=config.getInitParameter("User") %><br>  application Object :<strong>User context param value</strong>: <%=application.getInitParameter("User") %><br>  session Object :<strong>User Session ID</strong>: <%=session.getId() %><br>  pageContext Object :<% pageContext.setAttribute("Test", "Test Value"); %> <strong>PageContext attribute</strong>: {Name="Test",Value="<%=pageContext.getAttribute("Test") %>"}<br>  page Object :<strong>Generated Servlet Name</strong>: <%=page.getClass().getName() %>  exception Object :<strong>Exception occured</strong>: <%=exception %><br>',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (531, 'Какие неявные объекты не доступны в обычной JSP странице?',
        'Неявный объект исключений JSP недоступен в обычных JSP страницах и используется на страницах ошибок JSP (errorpage) только для того, чтобы перехватить исключение, выброшенное JSP страницей и далее предоставить какую-либо полезную информацию клиенту.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (532, 'Что вы знаете о PageContext и какие преимущества его использования?',
        'Неявный объект JSP - экземпляр класса javax.servlet.jsp.PageContext предоставляет доступ ко всем пространствам имён, ассоциированным с JSP-страницей, а также к различным её атрибутам.Остальные неявные объекты добавляются к pageContext автоматически.Класс PageContext это абстрактный класс, а его экземпляр можно получить через вызов метода JspFactory.getPageContext(), и освободить через вызов метода JspFactory.releasePageContext().PageContext обладает следующим набором особенностей и возможностей:  единый API для обслуживания пространств имён различных областей видимости;  несколько удобных API для доступа к различным public-объектам;  механизм получения JspWriter для вывода;  механизм обслуживания использования сессии страницей;  механизм экспонирования («показа») атрибутов директивы page среде скриптинга;  механизмы направления или включения текущего запроса в другие компоненты приложения;  механизм обработки процессов исключений на страницах ошибок errorpage;',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (533, 'Как сконфигурировать параметры инициализации для JSP?',
        'Параметры инициализации для JSP задаются в web.xml файле аналогично сервлетам - элементами servlet и servlet-mapping. Единственным отличием будет указание местонахождения JSP страницы:',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (534, 'Почему не рекомендуется использовать скриплеты (скриптовые элементы) в JSP?',
        'JSP страницы используются в основном  для целей отображения представления (view), а вся бизнес-логика (controller) и модель (model) должны быть реализованы в сервлетах или классах-моделях. Обязанность JSP страницы - создание HTML ответа из переданных через атрибуты параметров. Большая часть JSP содержит HTML код, а для того, чтобы помочь верстальщикам понять JSP код страницы предоставляется функционал элементов action, JSP EL, JSP Standart Tag Library. Именно их и необходимо использовать вместо скриптлетов для создания моста между (JSP)HTML и (JSP)Java частями.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (535, 'Можно ли определить класс внутри JSP страницы?',
        'Определить класс внутри JSP страницы можно, но это считается плохой практикой:', 9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (536, 'Что вы знаете о Языке выражений JSP (JSP Expression Language – EL)?',
        'JSP Expression Language (EL) — скриптовый язык выражений, который позволяет получить доступ к Java компонентам (JavaBeans) из JSP. Начиная с JSP 2.0 используется внутри JSP тегов для отделения Java кода от JSP для обеспечения лёгкого доступа к Java компонентам, уменьшая при этом количество кода Java в JSP-страницах, или даже полностью исключая его.Развитие EL происходило с целью сделать его более простым для дизайнеров, которые имеют минимальные познания в языке программирования Java. До появления языка выражений, JSP имел несколько специальных тегов таких как скриптлеты (англ.), выражения и т. п. которые позволяли записывать Java код непосредственно на странице. С использованием языка выражений веб-дизайнер должен знать только то, как организовать вызов соответствующих java-методов.Язык выражений JSP 2.0 включает:  Создание и изменение переменных.  Управление потоком выполнения программы: ветвление, выполнение различных типов итераций и т.д.  Упрощенное обращение к встроенным JSP-объектам.  Возможность создавать собственные функции.Язык выражений используется внутри конструкции ${ ... }. Подобная конструкция может размещаться либо отдельно, либо в правой части выражения установки атрибута тега.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (537, 'Какие типы EL операторов вы знаете?',
        'Операторы в EL поддерживают наиболее часто используемые манипуляции данными.Типы операторов:  Стандартные операторы отношения: == (или eq), != (или neq), < (или lt), > (или gt), <= (или le), >= (или ge).  Арифметические операторы:  , –, *, / (или div), % (или mod).  Логические операторы: && (или and), || (или or), ! (или not).  Оператор empty – используется для проверки переменной на null, или «пустое значение», который зависит от типа проверяемого объекта. Например, нулевая длина для строки или нулевой размер для коллекции.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (538, 'Назовите неявные, внутренние объекты JSP EL и их отличия от объектов JSP.',
        'Язык выражений JSP предоставляет множество неявных объектов, которые можно использовать для получения атрибутов в различных областях видимости (scopes) и для значений параметров. Важно отметить, что они отличаются от неявных объектов JSP и содержат атрибуты в заданной области видимости. Наиболее часто использующийся implicit object в JSP EL и JSP page — это объект pageContext. Ниже представлена таблица неявных объектов JSP EL.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (539, 'Как отключить возможность использования EL в JSP?',
        'Для игнорирования выполнения языка выражений на странице существует два способа:  использовать директиву <%@ page isELIgnored = «true» %>,  настроить web.xml (лучше подходит для отключения EL сразу на нескольких страницах):',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (540, 'Как узнать тип HTTP метода используя JSP EL?', '${pageContext.request.method}.', 9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (541, 'Что такое JSTL (JSP Standard tag library)?',
        'JavaServer Pages Standard Tag Library, JSTL, Стандартная библиотека тегов JSP — расширение спецификации JSP (конечный результат JSR 52), добавляющее библиотеку JSP тегов для общих нужд, таких как разбор XML данных, условная обработка, создание циклов и поддержка интернационализации.JSTL является альтернативой такому виду встроенной в JSP логики, как скриплеты (прямые вставки Java кода). Использование стандартизованного множества тегов предпочтительнее, поскольку получаемый код легче поддерживать и проще отделять бизнес-логику от логики отображения.Для использования JSTL тегов необходимо:  подключить зависимости, например в pom.xml:  указать пространство имен основных тегов JSTL через указание на JSP странице код:',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (542, 'Из каких групп тегов состоит библиотека JSTL?',
        'Группы тегов JSTL согласно их функциональности:  Core Tags предоставляют возможности итерации, обработки исключений, URL, forward, redirect response и т.д.  Formatting Tags и Localization Tags предоставляют возможности по форматированию чисел, дат и поддержки i18n локализации и resource bundles.  SQL Tags – поддержка работы с базами данных.  XML Tags используются для работы с XML документами: парсинга, преобразования данных, выполнения выражений XPath и т.д..  JSTL Functions Tags предоставляет набор функций, которые позволяют выполнять различные операции со строками и т.п. Например, по конкатенации или разбиению строк.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (543, 'Какая разница между <c:set> и <jsp:useBean>?',
        'Оба тега создают и помещают экземпляры в заданную область видимости, но <jsp:useBean> только создаёт экземпляр конкретного типа, а <c:set>, создав экземпляр, позволяет дополнительно извлекать значение, например, из параметров запроса, сессии и т. д.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (544, 'Чем отличается <c:import> от <jsp:include> и директивы <%@include %>?',
        'По сравнению с action-тегом <jsp:include> и директивой <%@include %> тег <c:import> обеспечивает более совершенное включение динамических ресурсов, т.к. получает доступ к источнику, чтение информации из которого происходит непосредственно без буферизации и контент включается в исходную JSP построчно.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (545, 'Как можно расширить функциональность JSP?', 'Что вы знаете о написании пользовательских JSP тегов?', 9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (546, 'Как сделать перенос строки в HTML средствами JSP?',
        'Для переноса строки можно использовать тег c:out и атрибут escapeXml, который отключает обработку HTML элементов. В этом случае браузер получит следующий код в виде строки и обработает элемент <br> как требуется:<c:out value="<br> creates a new line in HTML" escapeXml="true"></c:out>',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (547, 'Почему не нужно конфигурировать стандартные JSP теги в web.xml?',
        'Стандартные теги JSP не конфигурируются в web.xml, потому что tld файлы уже находятся внутри каталога /META-INF/ в jar файлах JSTL.Когда контейнер загружает веб-приложение и находит tld файлы в в jar файле в директории /META-INF/, то он автоматически настраивает их для непосредственного использования на JSP страницах. Остается только задать пространство имен на JSP странице.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (548, 'Как можно обработать ошибки JSP страниц?',
        'Для обработки исключений выброшенных на JSP странице достаточно лишь задать страницу ошибки JSP и при её создании установить значение page directive attribute isErrorPage в значение true. Таким образом будет предоставлен доступ к неявным объектам исключений в JSP и появится возможность передавать собственные, более информативные сообщения об ошибках клиенту. При этом настройка дескриптора развертывания выглядит так:',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (549, 'Как происходит обработка ошибок с помощью JSTL?',
        'Для перехвата и обработки исключений в служебных методах класса служат JSTL Core Tags c:catch и c:if.Тег c:catch перехватывает исключение и обертывает его в переменную exception, доступную для обработки в теге c:if:Обратите внимание что используется язык выражений JSP EL в теге c:if.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (550, 'Как конфигурируется JSP в дескрипторе развертывания.',
        'Для настройки различных параметров JSP страниц используется элемент jsp-config, который отвечает за:  управление элементами скриптлетов на странице;  управления выполнением в языке выражений;  определение шаблона URL для encoding;  определение размера буфера, который используется для объектов на странице;  обозначение групп ресурсов, соответствующих шаблону URL, которые должны быть обработаны как XML документ.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (551, 'Можно ли использовать Javascript на JSP странице?',
        'Да, это возможно. Несмотря на то, что JSP это серверная технология, на выходе она всё равно создает HTML страницу, на которую можно добавлять Javascript и CSS.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (552, 'Всегда ли создается объект сессии на JSP странице, можно ли отключить его создание?',
        'Jsp-страница, по умолчанию, всегда создает сессию. Используя директиву page с атрибутом session можно изменить это поведение:<%@ page session ="false" %>',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (553, 'Какая разница между JSPWriter и сервлетным PrintWriter?',
        'PrintWriter является объектом отвечающим за запись содержания ответа на запрос. JspWriter использует объект PrintWriter для буферизации. Когда буфер заполняется или сбрасывается, JspWriter использует объект PrintWriter для записи содержания в ответ.',
        9);
INSERT INTO exercises (id, question, answer, topic_id)
VALUES (554, 'Опишите общие практические принципы работы с JSP.',
        'Хорошей практикой работы с технологией JSP является соблюдение следующих правил:  Следует избегать использования элементов скриптлетов на странице. Если элементы action, JSTL, JSP EL не удовлетворяют потребностям, то желательно написать собственный тег.  Рекомендуется использовать разные виды комментариев: так JSP комментарии необходимы для уровня кода и отладки, т.к. они не будут показаны клиенту.  Не стоит размещать какой-либо бизнес логики внутри JSP страницы. Страницы должны использоваться только для создания ответов клиенту.  Для повышения производительности лучше отключать создание сессии на странице, когда это не требуется.  Директивы taglib, page в начале JSP страницы улучшают читабельность кода.  Следует правильно использовать директиву include и элемент jsp:include action. Первая используется для статических ресурсов, а второй для динамических ресурсов времени выполнения.  Обработку исключений нужно производить с помощью страниц ошибок. Это помогает избегать запуска специальных служебных методов и может повысить производительность.  Использующиеся CSS и JavaScript должны быть разнесены в разные файлы и подключаться в начале страницы.  В большинстве случаев JSTL должно хватать для всех нужд. Если это не так, то в начале следует проанализировать логику своего приложения, и попробовать перенести выполнения кода в сервлет, а далее с помощью установки атрибутов использовать на JSP странице только результат.',
        9);
